    79→}
    80→
    81→/**
    82→ * Fetch all proposals from the user's "Proposals List" field
    83→ * This field contains an array of proposal IDs associated with the user
    84→ * Following the principle: "fetch the user's proposal's list" from user table
    85→ *
    86→ * @param {string} userId - The user's _id
    87→ * @returns {Promise<Array>} Array of proposal objects
    88→ */
    89→export async function fetchProposalsByGuest(userId) {
    90→  try {
    91→    // Step 1: Fetch the user to get their Proposals List
    92→    const { data: userData, error: userError } = await supabase
    93→      .from('user')
