    95→      .eq('_id', userId)
    96→      .single();
    97→
    98→    if (userError) {
    99→      console.error('Error fetching user:', userError);
   100→      return [];
   101→    }
   102→
   103→    const proposalIds = userData?.['Proposals List'];
   104→
   105→    if (!proposalIds || !Array.isArray(proposalIds) || proposalIds.length === 0) {
   106→      console.log('No proposals found in user\'s Proposals List');
   107→      return [];
   108→    }
   109→
   110→    console.log(`📋 Found ${proposalIds.length} proposal IDs in user's Proposals List`);
   111→
   112→    // Step 2: Fetch all proposals by their IDs
   113→    const { data: proposalsData, error: proposalsError } = await supabase
   114→      .from('proposal')
   115→      .select('*')
   116→      .in('_id', proposalIds)
   117→      .or('Deleted.is.null,Deleted.eq.false')
   118→      .order('Created Date', { ascending: false });
   119→
   120→    if (proposalsError) {
   121→      console.error('Error fetching proposals:', proposalsError);
   122→      return [];
   123→    }
   124→
   125→    console.log(`✅ Successfully fetched ${proposalsData?.length || 0} proposals from ${proposalIds.length} IDs`);
   126→
   127→    return proposalsData || [];
   128→  } catch (err) {
   129→    console.error('Exception fetching proposals:', err);
   130→    return [];
   131→  }
   132→}
   133→
   134→/**
   135→ * Fetch move-in date and reservation span from user's most recent proposal
   136→ * for pre-populating new proposal creation flows.
   137→ *
   138→ * @param {string} userId - The user's _id
   139→ * @returns {Promise<{moveInDate: string|null, reservationSpanWeeks: number|null}|null>}
   140→ *          Returns object with last proposal defaults, or null if no previous proposal exists
   141→ */
   142→export async function fetchLastProposalDefaults(userId) {
   143→  if (!userId) {
   144→    console.log('fetchLastProposalDefaults: No userId provided');
