/**
 * Proposal Data Fetcher Utilities
 * Handles URL parsing and data fetching for guest proposals page
 *
 * Based on the same pattern as listingDataFetcher.js but for user/proposal data
 */

import { supabase } from './supabase.js';

/**
 * Extract user ID from URL pathname
 * Supports multiple URL formats:
 * - /guest-proposals/userId (primary format)
 * - /guest-proposals.html/userId (alternative format)
 * - /guest-proposals?user=userId (query string fallback)
 *
 * @returns {string|null} User ID or null if not found
 */
export function getUserIdFromUrl() {
  // 1. Check query string: ?user=userId
  const urlParams = new URLSearchParams(window.location.search);
  const idFromQuery = urlParams.get('user');
  if (idFromQuery) return idFromQuery;

  // 2. Parse pathname for segment after 'guest-proposals'
  const pathSegments = window.location.pathname.split('/').filter(segment => segment);
  const proposalsSegmentIndex = pathSegments.findIndex(segment =>
    segment === 'guest-proposals' ||
    segment === 'guest-proposals.html'
  );

  if (proposalsSegmentIndex !== -1 && pathSegments[proposalsSegmentIndex + 1]) {
    const nextSegment = pathSegments[proposalsSegmentIndex + 1];
    // Return the segment if it doesn't contain a file extension
    if (!nextSegment.includes('.')) {
      return nextSegment;
    }
  }

  // 3. No user ID found in URL
  return null;
}

/**
 * Fetch user data from Supabase by user ID
 * @param {string} userId - The user's _id
 * @returns {Promise<object|null>} User data or null if not found
 */
export async function fetchUserById(userId) {
  try {
    const { data, error } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', userId)
      .single();

    if (error) {
      console.error('Error fetching user:', error);
      return null;
    }

    return data;
  } catch (err) {
    console.error('Exception fetching user:', err);
    return null;
  }
}

/**
 * Fetch all proposals from the user's "Proposals List" field
 * This field contains an array of proposal IDs associated with the user
 * Following the principle: "fetch the user's proposal's list" from user table
 *
 * @param {string} userId - The user's _id
 * @returns {Promise<Array>} Array of proposal objects
 */
export async function fetchProposalsByGuest(userId) {
  try {
    // Step 1: Fetch the user to get their Proposals List
    const { data: userData, error: userError } = await supabase
      .from('user')
      .select('"Proposals List"')
      .eq('_id', userId)
      .single();

    if (userError) {
      console.error('Error fetching user:', userError);
      return [];
    }

    const proposalIds = userData?.['Proposals List'];

    if (!proposalIds || !Array.isArray(proposalIds) || proposalIds.length === 0) {
      console.log('No proposals found in user\'s Proposals List');
      return [];
    }

    console.log(`📋 Found ${proposalIds.length} proposal IDs in user's Proposals List`);

    // Step 2: Fetch all proposals by their IDs
    const { data: proposalsData, error: proposalsError } = await supabase
      .from('proposal')
      .select('*')
      .in('_id', proposalIds)
      .or('Deleted.is.null,Deleted.eq.false')
      .order('Created Date', { ascending: false });

    if (proposalsError) {
      console.error('Error fetching proposals:', proposalsError);
      return [];
    }

    console.log(`✅ Successfully fetched ${proposalsData?.length || 0} proposals from ${proposalIds.length} IDs`);

    return proposalsData || [];
  } catch (err) {
    console.error('Exception fetching proposals:', err);
    return [];
  }
}

/**
 * Fetch move-in date and reservation span from user's most recent proposal
 * for pre-populating new proposal creation flows.
 *
 * @param {string} userId - The user's _id
 * @returns {Promise<{moveInDate: string|null, reservationSpanWeeks: number|null}|null>}
 *          Returns object with last proposal defaults, or null if no previous proposal exists
 */
export async function fetchLastProposalDefaults(userId) {
  if (!userId) {
    console.log('fetchLastProposalDefaults: No userId provided');
    return null;
  }

  try {
    const { data, error } = await supabase
      .from('proposal')
      .select('"Move in range start", "Reservation Span (Weeks)"')
      .eq('Guest', userId)
      .or('Deleted.is.null,Deleted.eq.false')
      .order('Created Date', { ascending: false })
      .limit(1)
      .single();

    if (error || !data) {
      console.log('No previous proposal found for pre-population');
      return null;
    }

    console.log('Found last proposal defaults:', {
      moveInDate: data['Move in range start'],
      reservationSpanWeeks: data['Reservation Span (Weeks)']
    });

    return {
      moveInDate: data['Move in range start'] || null,
      reservationSpanWeeks: data['Reservation Span (Weeks)'] || null
    };
  } catch (err) {
    console.warn('Error fetching last proposal defaults:', err);
    return null;
  }
}

/**
 * Load complete details for a specific proposal
 * Enriches proposal with:
 * - Listing data
 * - Host user data
 * - House rules
 * - Virtual meeting data
 *
 * @param {object} proposal - Base proposal object
 * @returns {Promise<object>} Enriched proposal object
 */
export async function loadProposalDetails(proposal) {
  try {
    const enrichedProposal = { ...proposal };

    // Load listing
    if (proposal.Listing) {
      const { data: listingData, error: listingError } = await supabase
        .from('listing')
        .select('*')
        .eq('_id', proposal.Listing)
        .single();

      if (!listingError && listingData) {
        enrichedProposal._listing = listingData;
      }
    }

    // Load guest user (from proposal's Guest field)
    if (proposal.Guest) {
      const { data: guestData, error: guestError } = await supabase
        .from('user')
        .select(`
          _id,
          "Name - First",
          "Name - Full",
          "Profile Photo",
          "About Me / Bio",
          "email as text",
          "Phone Number (as text)",
          "Verify - Linked In ID",
          "Verify - Phone",
          "is email confirmed",
          "user verified?"
        `)
        .eq('_id', proposal.Guest)
        .single();

      if (!guestError && guestData) {
        enrichedProposal._guest = guestData;
      }
    }

    // Load host user (from listing creator)
    if (enrichedProposal._listing?.['Created By']) {
      const { data: hostData, error: hostError } = await supabase
        .from('user')
        .select(`
          _id,
          "Name - First",
          "Name - Full",
          "Profile Photo",
          "About Me / Bio",
          "email as text",
          "Phone Number (as text)",
          "Verify - Linked In ID",
          "Verify - Phone",
          "is email confirmed",
          "user verified?"
        `)
        .eq('_id', enrichedProposal._listing['Created By'])
        .single();

      if (!hostError && hostData) {
        enrichedProposal._host = hostData;
      }
    }

    // Load house rules
    if (proposal['House Rules'] && Array.isArray(proposal['House Rules']) && proposal['House Rules'].length > 0) {
      const { data: rulesData, error: rulesError } = await supabase
        .schema('reference_table')
        .from('zat_features_houserule')
        .select('_id, Name, Icon')
        .in('_id', proposal['House Rules']);

      if (!rulesError && rulesData) {
        enrichedProposal._houseRules = rulesData;
      }
    }

    // Load virtual meeting
    if (proposal['virtual meeting']) {
      const { data: vmData, error: vmError } = await supabase
        .from('virtualmeetingschedulesandlinks')
        .select('*')
        .eq('_id', proposal['virtual meeting'])
        .single();

      if (!vmError && vmData) {
        enrichedProposal._virtualMeeting = vmData;
      }
    }

    return enrichedProposal;
  } catch (err) {
    console.error('Error loading proposal details:', err);
    return proposal;
  }
}

/**
 * Workflow: Load complete proposal details with all enrichments.
 *
 * @intent Orchestrate loading and processing of proposal data from Supabase.
 * @rule Separates infrastructure (fetching) from domain logic (processing).
 * @rule Fetches raw data, then transforms via processors.
 * @rule Returns processed, validated proposal object ready for UI consumption.
 *
 * This workflow replaces the mixed fetcher/transformer pattern from proposalDataFetcher.js
 * by explicitly separating:
 * - Data fetching (infrastructure - still uses Supabase client)
 * - Data processing (domain logic - uses Logic Core processors)
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.rawProposal - Base proposal object (minimal data).
 * @param {object} [params.processListingData] - Listing processor function.
 * @param {object} [params.processUserData] - User processor function.
 * @param {object} [params.processProposalData] - Proposal processor function.
 * @returns {Promise<object>} Fully enriched and processed proposal object.
 *
 * @throws {Error} If rawProposal is missing.
 * @throws {Error} If critical data cannot be loaded.
 *
 * @example
 * import { loadProposalDetailsWorkflow } from '../logic/workflows/booking/loadProposalDetailsWorkflow.js'
 * import { processProposalData } from '../logic/processors/proposal/processProposalData.js'
 * import { processUserData } from '../logic/processors/user/processUserData.js'
 * import { processListingData } from '../logic/processors/listing/processListingData.js'
 * import { supabase } from '../lib/supabase.js'
 *
 * const enrichedProposal = await loadProposalDetailsWorkflow({
 *   supabase,
 *   rawProposal: { _id: 'abc123', Listing: 'xyz', Guest: '456' },
 *   processProposalData,
 *   processUserData,
 *   processListingData
 * })
 */
export async function loadProposalDetailsWorkflow({
  supabase,
  rawProposal,
  processListingData,
  processUserData,
  processProposalData
}) {
  // Validation
  if (!rawProposal) {
    throw new Error('loadProposalDetailsWorkflow: rawProposal is required')
  }

  if (!supabase) {
    throw new Error('loadProposalDetailsWorkflow: supabase client is required')
  }

  // Step 1: Fetch listing data
  let processedListing = null
  if (rawProposal.Listing) {
    const { data: listingData, error: listingError } = await supabase
      .from('listing')
      .select('*')
      .eq('_id', rawProposal.Listing)
      .single()

    if (!listingError && listingData) {
      // Process listing if processor provided
      if (processListingData) {
        try {
          processedListing = processListingData({ rawListing: listingData })
        } catch (err) {
          console.warn('Warning: Failed to process listing data:', err.message)
          // Continue without processed listing - UI will handle gracefully
        }
      } else {
        // No processor provided, use raw data
        processedListing = listingData
      }
    }
  }

  // Step 2: Fetch guest user data
  let processedGuest = null
  if (rawProposal.Guest) {
    const { data: guestData, error: guestError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', rawProposal.Guest)
      .single()

    if (!guestError && guestData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedGuest = processUserData(guestData)
        } catch (err) {
          console.warn('Warning: Failed to process guest data:', err.message)
        }
      } else {
        processedGuest = guestData
      }
    }
  }

  // Step 3: Fetch host user data (from listing creator)
  let processedHost = null
  if (processedListing && processedListing['Created By']) {
    const { data: hostData, error: hostError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', processedListing['Created By'])
      .single()

    if (!hostError && hostData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedHost = processUserData(hostData)
        } catch (err) {
          console.warn('Warning: Failed to process host data:', err.message)
        }
      } else {
        processedHost = hostData
      }
    }
  }

  // Step 4: Fetch house rules (if any)
  let houseRules = null
  if (
    rawProposal['House Rules'] &&
    Array.isArray(rawProposal['House Rules']) &&
    rawProposal['House Rules'].length > 0
  ) {
    const { data: rulesData, error: rulesError } = await supabase
      .schema('reference_table')
      .from('zat_features_houserule')
      .select('_id, Name, Icon')
      .in('_id', rawProposal['House Rules'])

    if (!rulesError && rulesData) {
      houseRules = rulesData
    }
  }

  // Step 5: Fetch virtual meeting data (if any)
  let virtualMeeting = null
  if (rawProposal['virtual meeting']) {
    const { data: vmData, error: vmError } = await supabase
      .from('virtualmeetingschedulesandlinks')
      .select('*')
      .eq('_id', rawProposal['virtual meeting'])
      .single()

    if (!vmError && vmData) {
      virtualMeeting = vmData
    }
  }

  // Step 6: Process proposal with all enriched data
  let processedProposal
  if (processProposalData) {
    try {
      processedProposal = processProposalData({
        rawProposal,
        listing: processedListing,
        guest: processedGuest,
        host: processedHost
      })
    } catch (err) {
      // If processor throws, this is a critical error
      throw new Error(`Failed to process proposal data: ${err.message}`)
    }
  } else {
    // No processor provided, create basic structure
    processedProposal = {
      ...rawProposal,
      _listing: processedListing,
      _guest: processedGuest,
      _host: processedHost
    }
  }

  // Step 7: Attach additional enrichments
  if (houseRules) {
    processedProposal._houseRules = houseRules
  }

  if (virtualMeeting) {
    processedProposal._virtualMeeting = virtualMeeting
  }

  return processedProposal
}

/**
 * Data Transformation Utilities for Guest Proposals
 * Transforms Bubble.io data structure to cleaner, more usable format
 *
 * Handles:
 * - Field name normalization (removes spaces, special characters)
 * - Nested object flattening
 * - Type conversions
 * - Default values for missing data
 */

import { formatPrice } from '../priceCalculations.js';

// Re-export for backwards compatibility
export { formatPrice };

/**
 * Transform user data from Bubble.io format
 *
 * @param {Object} rawUser - Raw user object from Supabase
 * @returns {Object} Transformed user object
 */
export function transformUserData(rawUser) {
  if (!rawUser) return null;

  return {
    id: rawUser._id,
    firstName: rawUser['Name - First'],
    lastName: rawUser['Name - Last'],
    fullName: rawUser['Name - Full'],
    profilePhoto: rawUser['Profile Photo'],
    proposalsList: rawUser['Proposals List']
  };
}

/**
 * Transform listing data from Bubble.io format
 *
 * @param {Object} rawListing - Raw listing object from Supabase
 * @returns {Object} Transformed listing object
 */
export function transformListingData(rawListing) {
  if (!rawListing) return null;

  // Extract address from JSONB structure
  const addressData = rawListing['Location - Address'];
  const addressString = typeof addressData === 'object' && addressData?.address
    ? addressData.address
    : (typeof addressData === 'string' ? addressData : null);

  return {
    id: rawListing._id,
    name: rawListing.Name,
    description: rawListing.Description,
    address: addressString,
    addressData: addressData, // Keep full JSONB for map coordinates
    borough: rawListing['Location - Borough'],
    hood: rawListing['Location - Hood'],
    boroughName: rawListing.boroughName, // Resolved name from lookup table
    hoodName: rawListing.hoodName, // Resolved name from lookup table
    city: rawListing['Location - City'],
    state: rawListing['Location - State'],
    zipCode: rawListing['Location - Zip Code'],
    photos: rawListing['Features - Photos'],
    featuredPhotoUrl: rawListing.featuredPhotoUrl, // Featured photo from listing_photo table
    houseRules: rawListing.houseRules || [], // Use resolved house rules from query layer
    checkInTime: rawListing['NEW Date Check-in Time'],
    checkOutTime: rawListing['NEW Date Check-out Time']
  };
}

/**
 * Transform host data from Bubble.io format
 *
 * @param {Object} rawHost - Raw host object from Supabase
 * @returns {Object} Transformed host object
 */
export function transformHostData(rawHost) {
  if (!rawHost) return null;

  return {
    id: rawHost._id,
    firstName: rawHost['Name - First'],
    lastName: rawHost['Name - Last'],
    fullName: rawHost['Name - Full'],
    profilePhoto: rawHost['Profile Photo'],
    bio: rawHost['About Me / Bio'],
    linkedInVerified: rawHost['Verify - Linked In ID'],
    phoneVerified: rawHost['Verify - Phone'],
    userVerified: rawHost['user verified?']
  };
}

/**
 * Transform guest data from Bubble.io format
 *
 * @param {Object} rawGuest - Raw guest object from Supabase
 * @returns {Object} Transformed guest object
 */
export function transformGuestData(rawGuest) {
  if (!rawGuest) return null;

  return {
    id: rawGuest._id,
    firstName: rawGuest['Name - First'],
    lastName: rawGuest['Name - Last'],
    fullName: rawGuest['Name - Full'],
    profilePhoto: rawGuest['Profile Photo'],
    bio: rawGuest['About Me / Bio'],
    linkedInVerified: rawGuest['Verify - Linked In ID'],
    phoneVerified: rawGuest['Verify - Phone'],
    userVerified: rawGuest['user verified?']
  };
}

/**
 * Transform virtual meeting data from Bubble.io format
 *
 * @param {Object} rawVirtualMeeting - Raw virtual meeting object from Supabase
 * @returns {Object} Transformed virtual meeting object
 */
export function transformVirtualMeetingData(rawVirtualMeeting) {
  if (!rawVirtualMeeting) return null;

  return {
    id: rawVirtualMeeting._id,
    bookedDate: rawVirtualMeeting['booked date'],
    confirmedBySplitlease: rawVirtualMeeting.confirmedBySplitLease,
    meetingLink: rawVirtualMeeting['meeting link'],
    meetingDeclined: rawVirtualMeeting['meeting declined'],
    requestedBy: rawVirtualMeeting['requested by'],
    suggestedTimeslots: rawVirtualMeeting['suggested dates and times'], // JSONB array of ISO datetimes
    guestName: rawVirtualMeeting['guest name'],
    hostName: rawVirtualMeeting['host name'],
    proposalId: rawVirtualMeeting.proposal
  };
}

/**
 * Transform complete proposal data from Bubble.io format
 * Includes nested transformations for listing, host, and virtual meeting
 *
 * @param {Object} rawProposal - Raw proposal object from Supabase
 * @returns {Object} Transformed proposal object
 */
export function transformProposalData(rawProposal) {
  if (!rawProposal) return null;

  // Extract nested data
  const rawListing = rawProposal.listing;
  const rawHost = rawListing?.host;
  const rawGuest = rawProposal.guest;
  const rawVirtualMeeting = rawProposal.virtualMeeting;

  return {
    id: rawProposal._id,
    status: rawProposal.Status,
    deleted: rawProposal.Deleted,
    daysSelected: rawProposal['Days Selected'],
    nightsSelected: rawProposal['Nights Selected (Nights list)'],
    reservationWeeks: rawProposal['Reservation Span (Weeks)'],
    nightsPerWeek: rawProposal['nights per week (num)'],
    checkInDay: rawProposal['check in day'],
    checkOutDay: rawProposal['check out day'],
    moveInStart: rawProposal['Move in range start'],
    moveInEnd: rawProposal['Move in range end'],
    totalPrice: rawProposal['Total Price for Reservation (guest)'],
    nightlyPrice: rawProposal['proposal nightly price'],
    cleaningFee: rawProposal['cleaning fee'],
    damageDeposit: rawProposal['damage deposit'],
    counterOfferHappened: rawProposal['counter offer happened'],
    hcDaysSelected: rawProposal['hc days selected'],
    hcReservationWeeks: rawProposal['hc reservation span (weeks)'],
    hcTotalPrice: rawProposal['hc total price'],
    hcNightlyPrice: rawProposal['hc nightly price'],
    createdDate: rawProposal['Created Date'],
    modifiedDate: rawProposal['Modified Date'],
    aboutYourself: rawProposal.about_yourself,
    specialNeeds: rawProposal.special_needs,
    reasonForCancellation: rawProposal['reason for cancellation'],
    proposalStage: rawProposal['Proposal Stage'],
    rentalApplicationId: rawProposal['rental application'],
    virtualMeetingId: rawProposal['virtual meeting'],
    isFinalized: rawProposal['Is Finalized'],

    // House rules (resolved from query layer)
    houseRules: rawProposal.houseRules || [],

    // Nested transformed data
    listing: transformListingData(rawListing),
    host: transformHostData(rawHost),
    guest: transformGuestData(rawGuest),
    virtualMeeting: transformVirtualMeetingData(rawVirtualMeeting)
  };
}

/**
 * Get display text for proposal in dropdown
 * Format: "{host name} - {listing name}"
 * Shows the host who owns the listing, allowing guests to identify proposals by host
 *
 * @param {Object} proposal - Transformed proposal object
 * @returns {string} Display text for dropdown option
 */
export function getProposalDisplayText(proposal) {
  if (!proposal) return null;

  const hostName = proposal.host?.firstName || proposal.host?.fullName || 'Host';
  const listingName = proposal.listing?.name || 'Property';

  return `${hostName} - ${listingName}`;
}

/**
 * Format date for display
 *
 * @param {string|Date} date - Date value
 * @returns {string} Formatted date string
 */
export function formatDate(date) {
  if (!date) return null;

  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (isNaN(dateObj.getTime())) return null;

  return dateObj.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
}

import { getNightlyRateByFrequency } from './getNightlyRateByFrequency.js'
import { calculateFourWeekRent } from './calculateFourWeekRent.js'
import { calculateReservationTotal } from './calculateReservationTotal.js'
import { validateNumber } from '../../validators/pricingValidators.js'

/**
 * Calculate complete pricing breakdown for a listing rental.
 *
 * @intent Provide comprehensive price calculation including all fees.
 * @rule Combines nightly rate, 4-week rent, reservation total, and fees.
 * @rule All calculations must succeed or throw - no partial results.
 *
 * @param {object} params - Named parameters.
 * @param {object} params.listing - Listing object with all pricing fields.
 * @param {number} params.nightsPerWeek - Nights selected per week (2-7).
 * @param {number} params.reservationWeeks - Total reservation span in weeks.
 * @returns {object} Complete pricing breakdown with all calculated values.
 *
 * @throws {Error} If any required parameter is missing or invalid.
 * @throws {Error} If any calculation in the chain fails.
 *
 * @example
 * const breakdown = calculatePricingBreakdown({
 *   listing: { '💰Nightly Host Rate for 4 nights': 100, '💰Cleaning Cost / Maintenance Fee': 50 },
 *   nightsPerWeek: 4,
 *   reservationWeeks: 13
 * })
 * // => { nightlyPrice: 100, fourWeekRent: 1600, reservationTotal: 5200, ... }
 */
export function calculatePricingBreakdown({ listing, nightsPerWeek, reservationWeeks }) {
  // No Fallback: Validate all inputs
  if (!listing || typeof listing !== 'object') {
    throw new Error(
      'calculatePricingBreakdown: listing must be a valid object'
    )
  }

  validateNumber(nightsPerWeek, 'nightsPerWeek', 'calculatePricingBreakdown')
  validateNumber(reservationWeeks, 'reservationWeeks', 'calculatePricingBreakdown')

  // Get nightly rate (throws if not found)
  const nightlyPrice = getNightlyRateByFrequency({
    listing,
    nightsSelected: nightsPerWeek
  })

  // Calculate 4-week rent (throws on invalid input)
  const fourWeekRent = calculateFourWeekRent({
    nightlyRate: nightlyPrice,
    frequency: nightsPerWeek
  })

  // Calculate reservation total (throws on invalid input)
  const reservationTotal = calculateReservationTotal({
    fourWeekRent,
    totalWeeks: reservationWeeks
  })

  // Extract fees (with explicit validation)
  const cleaningFee = extractFee(
    listing['💰Cleaning Cost / Maintenance Fee'],
    'Cleaning Fee'
  )

  const damageDeposit = extractFee(
    listing['💰Damage Deposit'],
    'Damage Deposit'
  )

  // Calculate grand total
  const grandTotal = reservationTotal + cleaningFee

  return {
    nightlyPrice,
    fourWeekRent,
    reservationTotal,
    cleaningFee,
    damageDeposit,
    grandTotal,
    valid: true
  }
}

/**
 * Extract and validate a fee value from listing.
 * Returns 0 for explicitly missing optional fees (null/undefined).
 * Throws for invalid fee values.
 *
 * @param {any} feeValue - The fee value from listing
 * @param {string} feeName - Name of fee for error messages
 * @returns {number} Validated fee amount (0 if optional and missing)
 * @throws {Error} If fee value is invalid (e.g., negative, NaN)
 */
function extractFee(feeValue, feeName) {
  // Optional fees can be null/undefined - default to 0
  if (feeValue === null || feeValue === undefined) {
    return 0
  }

  const fee = Number(feeValue)

  if (isNaN(fee)) {
    throw new Error(
      `calculatePricingBreakdown: ${feeName} has invalid value ${feeValue}`
    )
  }

  if (fee < 0) {
    throw new Error(
      `calculatePricingBreakdown: ${feeName} cannot be negative, got ${fee}`
    )
  }

  return fee
}

