     1/**
     2 * Utility functions for formatting listing data
     3 * Implements the 4 conditional logic rules from Bubble
     4 */
     5
     6/**
     7 * Bathroom display mapping based on Bubble option set
     8 */
     9const bathroomDisplayMap = {
    10  1: '1 Bath',
    11  1.5: '1.5 Baths',
    12  2: '2 Baths',
    13  2.5: '2.5 Baths',
    14  3: '3 Baths',
    15  3.5: '3.5 Baths',
    16  4: '4 Baths',
    17  4.5: '4.5 Baths',
    18  5: '5 Baths',
    19  6: '6 Baths',
    20};
    21
    22/**
    23 * Get bathroom display text from numeric value
    24 * @param {number} count - Number of bathrooms (can be decimal for half baths)
    25 * @returns {string} Formatted bathroom display text
    26 */
    27export const getBathroomDisplay = (count) => {
    28  return bathroomDisplayMap[count] || `${count} Baths`;
    29};
    30
    31/**
    32 * Format bedroom and bathroom text according to Bubble's 4 conditional rules
    33 *
    34 * CONDITIONAL 1: When bedrooms = 1
    35 *   Display: " 1 bedroom  [bathrooms]  [kitchen]"
    36 *
    37 * CONDITIONAL 2: When bathrooms = 0
    38 *   Display: "[bedrooms] bedrooms" (bedroom info only)
    39 *
    40 * CONDITIONAL 3: When bedrooms > 1
    41 *   Display: " [bedrooms] bedrooms  [bathrooms]  [kitchen]"
    42 *
    43 * CONDITIONAL 4: When kitchen type is empty
    44 *   Display: " [bedrooms] bedroom(s)  [bathrooms]" (no kitchen)
    45 *
    46 * @param {number} bedrooms - Number of bedrooms
    47 * @param {number} bathrooms - Number of bathrooms
