     1/**
     2 * Display Utilities for Proposal Cards
     3 *
     4 * Pure functions for formatting proposal data for display.
     5 * Extracted from ProposalCard to enable reuse in ExpandableProposalCard.
     6 *
     7 * Day indices use JavaScript 0-based standard (0=Sun, 1=Mon, ..., 6=Sat)
     8 */
     9
    10/**
    11 * Day abbreviations (0-indexed: 0=Sun, 1=Mon, ..., 6=Sat)
    12 */
    13export const DAY_ABBREVS = ['S', 'M', 'T', 'W', 'T', 'F', 'S'];
    14export const DAY_NAMES = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
    15
    16/**
    17 * Format currency for display
    18 * @param {number|string} amount - Amount to format
    19 * @param {boolean} [showCents=false] - Whether to show cents
    20 * @returns {string} Formatted currency string
    21 */
    22export function formatCurrency(amount, showCents = false) {
    23  const num = typeof amount === 'string' ? parseFloat(amount) : amount;
    24  if (isNaN(num) || num === null || num === undefined) return '$0';
    25
    26  if (showCents) {
    27    return `$${num.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
    28  }
    29  return `$${Math.round(num).toLocaleString('en-US')}`;
    30}
    31
    32/**
    33 * Format date for display
    34 * @param {string|Date} dateValue - Date to format
    35 * @returns {string} Formatted date string (e.g., "Jan 15, 2025")
    36 */
    37export function formatDate(dateValue) {
    38  if (!dateValue) return 'TBD';
    39
    40  const date = dateValue instanceof Date ? dateValue : new Date(dateValue);
    41  if (isNaN(date.getTime())) return 'TBD';
    42
    43  return date.toLocaleDateString('en-US', {
    44    month: 'short',
    45    day: 'numeric',
    46    year: 'numeric'
    47  });
    48}
    49
    50/**
    51 * Get short status label for collapsed card display
    52 * @param {string} status - Raw status string
    53 * @returns {string} Short status label
    54 */
    55export function getShortStatusLabel(status) {
    56  if (!status) return 'Pending';
    57
    58  const normalizedStatus = status.trim();
    59
    60  // Suggested proposals: distinguish guest action vs host action
    61  if (normalizedStatus.includes('Split Lease - Pending Confirmation')) return 'SL Suggestion';
    62  if (normalizedStatus.includes('Split Lease - Awaiting Rental Application')) return 'Confirm + Apply';
    63
    64  if (normalizedStatus.includes('Counteroffer')) return 'Review Offer';
    65  if (normalizedStatus.includes('Accepted') || normalizedStatus.includes('Drafting')) return 'Accepted';
    66  if (normalizedStatus.includes('activated')) return 'Active';
    67  if (normalizedStatus.includes('Signed')) return 'Signed';
    68  if (normalizedStatus.includes('Awaiting Rental Application')) return 'Apply Now';
    69  if (normalizedStatus.includes('Rental Application Submitted')) return 'Under Review';
    70  if (normalizedStatus.includes('Host Review')) return 'Host Review';
    71  if (normalizedStatus.includes('Lease Documents Sent for Review')) return 'Review Docs';
    72  if (normalizedStatus.includes('Lease Documents Sent for Signatures')) return 'Sign Lease';
    73  if (normalizedStatus.includes('Cancelled')) return 'Cancelled';
    74  if (normalizedStatus.includes('Rejected')) return 'Declined';
    75
    76  return 'Pending';
    77}
    78
    79/**
    80 * Get status badge CSS class
    81 * @param {string} status - Raw status string
    82 * @returns {string} CSS class name
    83 */
    84export function getStatusBadgeClass(status) {
    85  if (!status) return '';
    86
    87  const normalizedStatus = status.trim();
    88
    89  // Action required: Suggested proposals pending guest confirmation
    90  if (normalizedStatus.includes('Split Lease - Pending Confirmation')) {
    91    return 'action-required';
    92  }
    93
    94  // Success states (accepted, completed)
    95  if (normalizedStatus.includes('Accepted') ||
    96      normalizedStatus.includes('activated') ||
    97      normalizedStatus.includes('Signed')) {
    98    return 'success';
    99  }
   100
   101  // Attention states (counteroffer, awaiting action)
   102  if (normalizedStatus.includes('Counteroffer') ||
   103      normalizedStatus.includes('Awaiting') ||
   104      normalizedStatus.includes('Review')) {
   105    return 'attention';
   106  }
   107
   108  return '';
   109}
   110
   111/**
   112 * Check if proposal is SL-suggested
   113 * @param {string} status - Raw status string
   114 * @returns {boolean}
   115 */
   116export function isSLSuggested(status) {
   117  if (!status) return false;
   118  const normalizedStatus = status.trim();
   119  return normalizedStatus.includes('Submitted for guest by Split Lease');
   120}
   121
   122/**
   123 * Check if proposal is pending confirmation (not yet confirmed by guest)
   124 * @param {string} status - Raw status string
   125 * @returns {boolean}
   126 */
   127export function isPendingConfirmation(status) {
   128  if (!status) return false;
   129  const normalizedStatus = status.trim();
   130  return normalizedStatus.includes('Pending Confirmation');
   131}
   132
   133/**
   134 * Check if proposal is cancelled or rejected
   135 * @param {string} status - Raw status string
   136 * @returns {boolean}
   137 */
   138export function isTerminalStatus(status) {
   139  if (!status) return false;
   140  const normalizedStatus = status.trim();
   141  return normalizedStatus.includes('Cancelled') || normalizedStatus.includes('Rejected');
   142}
   143
   144/**
   145 * Extract listing photo URL with fallback
   146 * @param {Object} listing - Listing object
   147 * @returns {string|null} Photo URL or null
   148 */
   149export function getListingPhoto(listing) {
   150  if (!listing) return null;
   151
   152  return listing.featuredPhotoUrl ||
   153    listing['Features - Photos']?.[0] ||
   154    null;
   155}
   156
   157/**
   158 * Get host display name
   159 * @param {Object} host - Host object (Bubble.io format)
   160 * @returns {string} Display name
   161 */
   162export function getHostDisplayName(host) {
   163  if (!host) return 'Host';
   164
   165  // Bubble.io uses 'Name - First' and 'Name - Full' field names
   166  return host['Name - First'] ||
   167    host['Name - Full'] ||
   168    host.firstName ||
   169    host.name ||
   170    'Host';
   171}
   172
   173/**
   174 * Get host profile photo URL
   175 * @param {Object} host - Host object (Bubble.io format)
   176 * @returns {string|null} Photo URL or null
   177 */
   178export function getHostProfilePhoto(host) {
   179  if (!host) return null;
   180
   181  return host['Profile Photo'] || null;
   182}
   183
   184/**
   185 * Build meta text for collapsed card (e.g., "5 days  12 weeks")
   186 * @param {Array} daysSelected - Array of selected day indices
   187 * @param {string|number} weeks - Number of weeks
   188 * @returns {string} Meta text
   189 */
   190export function buildMetaText(daysSelected, weeks) {
   191  const daysCount = Array.isArray(daysSelected) ? daysSelected.length : 0;
   192  if (weeks) {
   193    return `${daysCount} day${daysCount !== 1 ? 's' : ''}  ${weeks} weeks`;
   194  }
   195  return `${daysCount} day${daysCount !== 1 ? 's' : ''}`;
   196}
   197
   198/**
   199 * Format weekly price display
   200 * @param {number} nightlyPrice - Price per night
   201 * @param {number} daysCount - Number of days per week
   202 * @returns {string} Formatted weekly price
   203 */
   204export function formatWeeklyPrice(nightlyPrice, daysCount) {
   205  if (!nightlyPrice || !daysCount) return '$0/wk';
   206  const weekly = nightlyPrice * daysCount;
   207  return `${formatCurrency(weekly)}/wk`;
   208}
   209
   210/**
   211 * Get progress stage number from status
   212 * @param {string} status - Raw status string
   213 * @returns {number} Stage 1-6 or 0 if unknown
   214 */
   215export function getProgressStage(status) {
   216  if (!status) return 0;
   217
   218  const normalizedStatus = status.trim();
   219
   220  // Stage 1: Initial/Submitted
   221  if (normalizedStatus.includes('Awaiting Rental Application') ||
   222      normalizedStatus.includes('Pending') ||
   223      normalizedStatus.includes('Split Lease - Pending Confirmation')) {
   224    return 1;
   225  }
   226
   227  // Stage 2: Application submitted
   228  if (normalizedStatus.includes('Rental Application Submitted')) {
   229    return 2;
   230  }
   231
   232  // Stage 3: Under review / counteroffer
   233  if (normalizedStatus.includes('Host Review') ||
   234      normalizedStatus.includes('Counteroffer')) {
   235    return 3;
   236  }
   237
   238  // Stage 4: Accepted / Documents
   239  if (normalizedStatus.includes('Accepted') ||
   240      normalizedStatus.includes('Drafting') ||
   241      normalizedStatus.includes('Documents Sent for Review') ||
   242      normalizedStatus.includes('Reviewing')) {
   243    return 4;
   244  }
   245
   246  // Stage 5: Signing
   247  if (normalizedStatus.includes('Signatures')) {
   248    return 5;
   249  }
   250
   251  // Stage 6: Payment / Activated
   252  if (normalizedStatus.includes('Payment') ||
   253      normalizedStatus.includes('activated')) {
   254    return 6;
   255  }
   256
   257  return 0;
   258}
   259
   260/**
   261 * Get progress stage labels for display
   262 * @returns {Array<string>} Array of stage labels
   263 */
   264export function getProgressStageLabels() {
   265  return [
   266    'Submitted',
   267    'Application',
   268    'Review',
   269    'Accepted',
   270    'Signing',
   271    'Active'
   272  ];
   273}
