/**
 * Centralized Date Formatting Utilities
 *
 * Consolidates all date formatting logic to ensure consistency across the application.
 * All formatDate implementations throughout the codebase should delegate to these functions.
 *
 * @module lib/dateFormatters
 */

/**
 * Format a date for display with configurable format and fallback
 * @param {string|Date|null|undefined} dateValue - Date to format
 * @param {Object} [options] - Formatting options
 * @param {string} [options.format='medium'] - Format type: 'short' (M/D/YY), 'medium' (Jan 15, 2025), 'long' (January 15, 2025), 'iso' (2025-01-15)
 * @param {string} [options.fallback=''] - Value to return for invalid/null dates
 * @returns {string} Formatted date string
 */
export function formatDateDisplay(dateValue, options = {}) {
  const { format = 'medium', fallback = '' } = options;

  if (!dateValue) return fallback;

  const date = dateValue instanceof Date ? dateValue : new Date(dateValue);
  if (isNaN(date.getTime())) return fallback;

  switch (format) {
    case 'short':
      // M/D/YY format
      const month = date.getMonth() + 1;
      const day = date.getDate();
      const year = date.getFullYear().toString().slice(-2);
      return `${month}/${day}/${year}`;

    case 'medium':
      // Jan 15, 2025 format
      return date.toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        year: 'numeric'
      });

    case 'long':
      // January 15, 2025 format
      return date.toLocaleDateString('en-US', {
        month: 'long',
        day: 'numeric',
        year: 'numeric'
      });

    case 'iso':
      // 2025-01-15 format
      return date.toISOString().split('T')[0];

    default:
      return date.toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        year: 'numeric'
      });
  }
}

/**
 * Format a datetime for display
 * @param {string|Date|null|undefined} dateValue - Datetime to format
 * @param {Object} [options] - Formatting options
 * @param {string} [options.fallback=''] - Value to return for invalid/null dates
 * @param {boolean} [options.includeTimezone=false] - Whether to include timezone
 * @returns {string} Formatted datetime string
 */
export function formatDateTimeDisplay(dateValue, options = {}) {
  const { fallback = '', includeTimezone = false } = options;

  if (!dateValue) return fallback;

  const date = dateValue instanceof Date ? dateValue : new Date(dateValue);
  if (isNaN(date.getTime())) return fallback;

  const formatOptions = {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: 'numeric',
    minute: '2-digit',
    timeZone: 'America/New_York'
  };

  if (includeTimezone) {
    formatOptions.timeZoneName = 'short';
  }

  return date.toLocaleString('en-US', formatOptions);
}

/**
 * Format a date range for display
 * @param {string|Date} start - Start date
 * @param {string|Date} end - End date
 * @param {Object} [options] - Formatting options
 * @returns {string} Formatted date range (e.g., "1/15/25 - 2/28/25")
 */
export function formatDateRange(start, end, options = {}) {
  const { format = 'short' } = options;
  const startFormatted = formatDateDisplay(start, { format, fallback: '' });
  const endFormatted = formatDateDisplay(end, { format, fallback: '' });

  if (!startFormatted && !endFormatted) return '';
  if (!startFormatted) return endFormatted;
  if (!endFormatted) return startFormatted;

  return `${startFormatted} - ${endFormatted}`;
}

/**
 * Day Indexing Utilities
 *
 * All day indices use JavaScript's 0-based numbering (matching Date.getDay()):
 *   0=Sunday, 1=Monday, 2=Tuesday, 3=Wednesday, 4=Thursday, 5=Friday, 6=Saturday
 *
 * NOTE: Bubble API conversion functions were removed after migrating the database
 * to use 0-indexed days natively. The database now stores days in JS format.
 */

/**
 * Validate that days array contains only valid 0-based indices
 * @param {number[]} days - Array of day indices to validate
 * @returns {boolean} True if all days are valid 0-based indices (0-6)
 */
export function isValidDaysArray(days) {
  if (!Array.isArray(days)) return false;
  return days.every(day => typeof day === 'number' && day >= 0 && day <= 6);
}

/**
 * Day names indexed by 0-based day number
 */
export const DAY_NAMES = [
  'Sunday',   // 0
  'Monday',   // 1
  'Tuesday',  // 2
  'Wednesday',// 3
  'Thursday', // 4
  'Friday',   // 5
  'Saturday'  // 6
];

/**
 * Get day name from 0-based index
 * @param {number} dayIndex - 0-based day index (0-6)
 * @returns {string} Day name or 'Unknown' if invalid
 */
export function getDayName(dayIndex) {
  if (typeof dayIndex !== 'number' || dayIndex < 0 || dayIndex > 6) {
    return 'Unknown';
  }
  return DAY_NAMES[dayIndex];
}

/**
 * Get short day name from 0-based index
 * @param {number} dayIndex - 0-based day index (0-6)
 * @returns {string} Short day name (Sun, Mon, etc.) or '???' if invalid
 */
export function getShortDayName(dayIndex) {
  const name = getDayName(dayIndex);
  return name === 'Unknown' ? '???' : name.slice(0, 3);
}

/**
 * Calculate days until target day of week
 * @.claude\plans\Deprecated\Context\Option Sets\ZEP - Curation Parameters(OS).md {number} fromDay - Current day (0-6)
 * @.claude\plans\Deprecated\Context\Option Sets\ZEP - Curation Parameters(OS).md {number} toDay - Target day (0-6)
 * @returns {number} Days until target (0-6)
 */
export function daysUntilDayOfWeek(fromDay, toDay) {
  return (toDay - fromDay + 7) % 7
}

/**
 * @deprecated Use workflows/proposals/cancelProposalWorkflow.js instead
 * This file is scheduled for removal. All imports should use:
 * import { executeCancelProposal } from '../proposals/cancelProposalWorkflow.js'
 */

// Re-export from canonical location for any remaining imports
export { executeCancelProposal as cancelProposalWorkflow } from '../proposals/cancelProposalWorkflow.js';

/**
 * Workflow: Load complete proposal details with all enrichments.
 *
 * @intent Orchestrate loading and processing of proposal data from Supabase.
 * @rule Separates infrastructure (fetching) from domain logic (processing).
 * @rule Fetches raw data, then transforms via processors.
 * @rule Returns processed, validated proposal object ready for UI consumption.
 *
 * This workflow replaces the mixed fetcher/transformer pattern from proposalDataFetcher.js
 * by explicitly separating:
 * - Data fetching (infrastructure - still uses Supabase client)
 * - Data processing (domain logic - uses Logic Core processors)
 *
 * @param {object} params - Named parameters.
 * @param {object} params.supabase - Supabase client instance.
 * @param {object} params.rawProposal - Base proposal object (minimal data).
 * @param {object} [params.processListingData] - Listing processor function.
 * @param {object} [params.processUserData] - User processor function.
 * @param {object} [params.processProposalData] - Proposal processor function.
 * @returns {Promise<object>} Fully enriched and processed proposal object.
 *
 * @throws {Error} If rawProposal is missing.
 * @throws {Error} If critical data cannot be loaded.
 *
 * @example
 * import { loadProposalDetailsWorkflow } from '../logic/workflows/booking/loadProposalDetailsWorkflow.js'
 * import { processProposalData } from '../logic/processors/proposals/processProposalData.js'
 * import { processUserData } from '../logic/processors/user/processUserData.js'
 * import { processListingData } from '../logic/processors/listing/processListingData.js'
 * import { supabase } from '../lib/supabase.js'
 *
 * const enrichedProposal = await loadProposalDetailsWorkflow({
 *   supabase,
 *   rawProposal: { _id: 'abc123', Listing: 'xyz', Guest: '456' },
 *   processProposalData,
 *   processUserData,
 *   processListingData
 * })
 */
export async function loadProposalDetailsWorkflow({
  supabase,
  rawProposal,
  processListingData,
  processUserData,
  processProposalData
}) {
  // Validation
  if (!rawProposal) {
    throw new Error('loadProposalDetailsWorkflow: rawProposal is required')
  }

  if (!supabase) {
    throw new Error('loadProposalDetailsWorkflow: supabase client is required')
  }

  // Step 1: Fetch listing data
  let processedListing = null
  if (rawProposal.Listing) {
    const { data: listingData, error: listingError } = await supabase
      .from('listing')
      .select('*')
      .eq('_id', rawProposal.Listing)
      .single()

    if (!listingError && listingData) {
      // Process listing if processor provided
      if (processListingData) {
        try {
          processedListing = processListingData({ rawListing: listingData })
        } catch (err) {
          console.warn('Warning: Failed to process listing data:', err.message)
          // Continue without processed listing - UI will handle gracefully
        }
      } else {
        // No processor provided, use raw data
        processedListing = listingData
      }
    }
  }

  // Step 2: Fetch guest user data
  let processedGuest = null
  if (rawProposal.Guest) {
    const { data: guestData, error: guestError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', rawProposal.Guest)
      .single()

    if (!guestError && guestData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedGuest = processUserData(guestData)
        } catch (err) {
          console.warn('Warning: Failed to process guest data:', err.message)
        }
      } else {
        processedGuest = guestData
      }
    }
  }

  // Step 3: Fetch host user data (from listing creator)
  let processedHost = null
  if (processedListing && processedListing['Created By']) {
    const { data: hostData, error: hostError } = await supabase
      .from('user')
      .select(`
        _id,
        "Name - First",
        "Name - Full",
        "Profile Photo",
        "About Me / Bio",
        "email as text",
        "Phone Number (as text)",
        "Verify - Linked In ID",
        "Verify - Phone",
        "is email confirmed",
        "user verified?"
      `)
      .eq('_id', processedListing['Created By'])
      .single()

    if (!hostError && hostData) {
      // Process user if processor provided
      if (processUserData) {
        try {
          processedHost = processUserData(hostData)
        } catch (err) {
          console.warn('Warning: Failed to process host data:', err.message)
        }
      } else {
        processedHost = hostData
      }
    }
  }

  // Step 4: Fetch house rules (if any)
  let houseRules = null
  if (
    rawProposal['House Rules'] &&
    Array.isArray(rawProposal['House Rules']) &&
    rawProposal['House Rules'].length > 0
  ) {
    const { data: rulesData, error: rulesError } = await supabase
      .schema('reference_table')
      .from('zat_features_houserule')
      .select('_id, Name, Icon')
      .in('_id', rawProposal['House Rules'])

    if (!rulesError && rulesData) {
      houseRules = rulesData
    }
  }

  // Step 5: Fetch virtual meeting data (if any)
  let virtualMeeting = null
  if (rawProposal['virtual meeting']) {
    const { data: vmData, error: vmError } = await supabase
      .from('virtualmeetingschedulesandlinks')
      .select('*')
      .eq('_id', rawProposal['virtual meeting'])
      .single()

    if (!vmError && vmData) {
      virtualMeeting = vmData
    }
  }

  // Step 6: Process proposal with all enriched data
  let processedProposal
  if (processProposalData) {
    try {
      processedProposal = processProposalData({
        rawProposal,
        listing: processedListing,
        guest: processedGuest,
        host: processedHost
      })
    } catch (err) {
      // If processor throws, this is a critical error
      throw new Error(`Failed to process proposal data: ${err.message}`)
    }
  } else {
    // No processor provided, create basic structure
    processedProposal = {
      ...rawProposal,
      _listing: processedListing,
      _guest: processedGuest,
      _host: processedHost
    }
  }

  // Step 7: Attach additional enrichments
  if (houseRules) {
    processedProposal._houseRules = houseRules
  }

  if (virtualMeeting) {
    processedProposal._virtualMeeting = virtualMeeting
  }

  return processedProposal
}

