/**
 * Price Calculation Utilities
 * Handles all pricing logic for view-split-lease page
 *
 * Usage:
 *   import { calculate4WeekRent, calculateReservationTotal } from './priceCalculations.js';
 */

/**
 * Calculate 4-week rent based on nightly price and selected nights
 * Formula: nightly price  nights per week  4 weeks
 * @param {number} nightlyPrice - Price per night
 * @param {number} nightsPerWeek - Number of nights selected per week
 * @returns {number} 4-week rent amount
 */
export function calculate4WeekRent(nightlyPrice, nightsPerWeek) {
  if (!nightlyPrice || !nightsPerWeek) return 0;
  return nightlyPrice * nightsPerWeek * 4;
}

/**
 * Calculate estimated reservation total
 * Formula: 4-week rent  (total weeks / 4)
 * @param {number} fourWeekRent - 4-week rent amount
 * @param {number} totalWeeks - Total reservation span in weeks
 * @returns {number} Estimated total cost
 */
export function calculateReservationTotal(fourWeekRent, totalWeeks) {
  if (!fourWeekRent || !totalWeeks) return 0;
  return fourWeekRent * (totalWeeks / 4);
}

/**
 * Get nightly price based on number of nights selected
 * Matches Bubble logic for price field selection
 * @param {object} listing - Listing object with price fields
 * @param {number} nightsSelected - Number of nights per week (2-7)
 * @returns {number|null} Nightly price
 */
export function getNightlyPriceForNights(listing, nightsSelected) {
  if (!listing || !nightsSelected) return null;

  // Price override takes precedence
  if (listing['Price Override']) {
    return listing['Price Override'];
  }

  // Map nights to price fields
  const priceFieldMap = {
    1: 'Nightly Host Rate for 1 night',
    2: 'Nightly Host Rate for 2 nights',
    3: 'Nightly Host Rate for 3 nights',
    4: 'Nightly Host Rate for 4 nights',
    5: 'Nightly Host Rate for 5 nights',
    7: 'Nightly Host Rate for 7 nights'
  };

  const fieldName = priceFieldMap[nightsSelected];
  if (fieldName && listing[fieldName]) {
    return listing[fieldName];
  }

  // Default to 4-night rate if available
  return listing['Nightly Host Rate for 4 nights'] || null;
}

/**
 * Format price as currency string
 * @param {number} amount - Dollar amount
 * @param {boolean} showCents - Whether to show cents (default: false)
 * @returns {string} Formatted price (e.g., "$1,234" or "$1,234.56")
 */
export function formatPrice(amount, showCents = false) {
  if (amount === null || amount === undefined || isNaN(amount)) {
    return '$0';
  }

  const formatted = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: showCents ? 2 : 0,
    maximumFractionDigits: showCents ? 2 : 0
  }).format(amount);

  return formatted;
}

/**
 * Calculate complete pricing breakdown
 * @param {object} listing - Listing object
 * @param {number} nightsPerWeek - Nights selected per week
 * @param {number} reservationWeeks - Total reservation span in weeks
 * @returns {object} Complete pricing breakdown
 */
export function calculatePricingBreakdown(listing, nightsPerWeek, reservationWeeks) {
  const nightlyPrice = getNightlyPriceForNights(listing, nightsPerWeek);

  if (!nightlyPrice) {
    return {
      nightlyPrice: null,
      fourWeekRent: null,
      reservationTotal: null,
      cleaningFee: listing['Cleaning Cost / Maintenance Fee'] || 0,
      damageDeposit: listing['Damage Deposit'] || 0,
      valid: false
    };
  }

  const fourWeekRent = calculate4WeekRent(nightlyPrice, nightsPerWeek);
  const reservationTotal = calculateReservationTotal(fourWeekRent, reservationWeeks);

  return {
    nightlyPrice,
    fourWeekRent,
    reservationTotal,
    cleaningFee: listing['Cleaning Cost / Maintenance Fee'] || 0,
    damageDeposit: listing['Damage Deposit'] || 0,
    grandTotal: reservationTotal + (listing['Cleaning Cost / Maintenance Fee'] || 0),
    valid: true
  };
}

/**
 * Validate if enough days are selected for price calculation
 * @param {number} daysSelected - Number of days selected
 * @returns {boolean} True if valid for pricing
 */
export function isValidForPricing(daysSelected) {
  return daysSelected >= 2 && daysSelected <= 7;
}

/**
 * Get price display message based on selection state
 * @param {number|null} daysSelected - Number of days selected
 * @returns {string} Display message
 */
export function getPriceDisplayMessage(daysSelected) {
  if (!daysSelected || daysSelected < 2) {
    return 'Please Select More Days';
  }
  if (daysSelected > 7) {
    return 'Please Select 7 Days or Less';
  }
  return null; // Valid selection
}

/**
 * Logic Constants Barrel Export
 *
 * Centralizes exports for all logic constants, making imports cleaner
 * and refactoring easier.
 *
 * Usage:
 *   import { PROPOSAL_STATUSES, PROPOSAL_STAGES, PRICING_CONSTANTS } from '../logic/constants';
 */

// Proposal Statuses
export {
  PROPOSAL_STATUSES,
  getStatusConfig,
  getStageFromStatus,
  getUsualOrder,
  shouldShowStatusBanner,
  getActionsForStatus,
  isActiveStatus,
  isTerminalStatus,
  isCompletedStatus,
  isSuggestedProposal,
  getStatusesByColor,
  getStatusesByStage,
  isPendingConfirmationProposal
} from './proposalStatuses.js';

// Proposal Stages
export {
  PROPOSAL_STAGES,
  getStageById,
  getStageByName,
  getStageProgress,
  getCompletedStages,
  getRemainingStages,
  isStageCompleted,
  isCurrentStage,
  isStagePending,
  getPreviousStage,
  getNextStage,
  formatStageDisplay,
  getAllStagesFormatted
} from './proposalStages.js';

// Pricing Constants
export {
  DEFAULT_ZAT_CONFIG,
  PRICE_TIERS,
  RENTAL_TYPES
} from './pricingConstants.js';

// Search Constants
export {
  SORT_OPTIONS,
  DEFAULT_FILTERS
} from './searchConstants.js';

// Review Categories
export {
  REVIEW_CATEGORIES,
  REVIEW_RATING_SCALE
} from './reviewCategories.js';

/**
 * Route Registry - Single Source of Truth for All Routes
 *
 * This file is the ONLY place where routes are defined. All routing configurations
 * (Vite dev server, Vite preview, Cloudflare _redirects, Cloudflare _routes.json)
 * are generated from this single source.
 *
 * Each route defines:
 * - path: The clean URL pattern (supports :param syntax for dynamic segments)
 * - file: The HTML file to serve
 * - protected: Whether authentication is required
 * - cloudflareInternal: Use _internal/ directory to avoid Cloudflare's 308 redirects
 * - internalName: Name for the _internal/ file (no .html extension)
 * - aliases: Additional URL patterns that map to the same file
 * - hasDynamicSegment: Whether the path has a dynamic segment (e.g., :id, :userId)
 *
 * @see docs/ROUTE_REGISTRY_IMPLEMENTATION_PLAN.md for full documentation
 */

export const routes = [
  // ===== HOMEPAGE =====
  {
    path: '/',
    file: 'index.html',
    aliases: ['/index', '/index.html'],
    protected: false,
    cloudflareInternal: false,
    hasDynamicSegment: false
  },

  // ===== INDEX DEV (development only) =====
  {
    path: '/index-dev',
    file: 'index-dev.html',
    aliases: ['/index-dev.html'],
    protected: false,
    cloudflareInternal: false,
    hasDynamicSegment: false,
    devOnly: true
  },

  // ===== SEARCH PAGES =====
  {
    path: '/search',
    file: 'search.html',
    aliases: ['/search.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'search-view',
    hasDynamicSegment: false
  },

  // ===== DYNAMIC ROUTES (WITH ID PARAMS) =====
  {
    path: '/view-split-lease',
    file: 'view-split-lease.html',
    aliases: ['/view-split-lease.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'listing-view',
    hasDynamicSegment: true,
    dynamicPattern: '/view-split-lease/:id'
  },
  {
    path: '/preview-split-lease',
    file: 'preview-split-lease.html',
    aliases: ['/preview-split-lease.html'],
    protected: true,
    cloudflareInternal: true,
    internalName: 'preview-listing-view',
    hasDynamicSegment: true,
    dynamicPattern: '/preview-split-lease/:id'
  },
  {
    path: '/guest-proposals',
    file: 'guest-proposals.html',
    aliases: ['/guest-proposals.html'],
    protected: true,
    cloudflareInternal: true,
    internalName: 'guest-proposals-view',
    hasDynamicSegment: true,
    dynamicPattern: '/guest-proposals/:userId',
    excludeFromFunctions: true // Explicitly excluded in _routes.json
  },
  {
    path: '/account-profile',
    file: 'account-profile.html',
    aliases: ['/account-profile.html'],
    protected: true,
    cloudflareInternal: true,
    internalName: 'account-profile-view',
    hasDynamicSegment: true,
    dynamicPattern: '/account-profile/:userId'
  },

  // ===== HELP CENTER (SPECIAL HANDLING) =====
  {
    path: '/help-center',
    file: 'help-center.html',
    aliases: ['/help-center.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'help-center-view',
    hasDynamicSegment: false
  },
  {
    path: '/help-center/:category',
    file: 'help-center-category.html',
    protected: false,
    cloudflareInternal: true,
    internalName: 'help-center-category-view',
    hasDynamicSegment: true,
    skipFileExtensionCheck: true // Special: /help-center/guests (not .html)
  },

  // ===== INFO PAGES =====
  {
    path: '/faq',
    file: 'faq.html',
    aliases: ['/faq.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'faq-view',
    hasDynamicSegment: false
  },
  {
    path: '/policies',
    file: 'policies.html',
    aliases: ['/policies.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'policies-view',
    hasDynamicSegment: false
  },
  {
    path: '/list-with-us',
    file: 'list-with-us.html',
    aliases: ['/list-with-us.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'list-with-us-view',
    hasDynamicSegment: false
  },
  {
    path: '/list-with-us-v2',
    file: 'list-with-us-v2.html',
    aliases: ['/list-with-us-v2.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'list-with-us-v2-view',
    hasDynamicSegment: false
  },
  {
    path: '/why-split-lease',
    file: 'why-split-lease.html',
    aliases: ['/why-split-lease.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'why-split-lease-view',
    hasDynamicSegment: false
  },
  {
    path: '/careers',
    file: 'careers.html',
    aliases: ['/careers.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'careers-view',
    hasDynamicSegment: false
  },
  {
    path: '/about-us',
    file: 'about-us.html',
    aliases: ['/about-us.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'about-us-view',
    hasDynamicSegment: false
  },
  {
    path: '/host-guarantee',
    file: 'host-guarantee.html',
    aliases: ['/host-guarantee.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'host-guarantee-view',
    hasDynamicSegment: false
  },
  {
    path: '/referral',
    file: 'referral.html',
    aliases: ['/referral.html', '/ref'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'referral-view',
    hasDynamicSegment: false
  },

  // ===== SUCCESS PAGES =====
  {
    path: '/guest-success',
    file: 'guest-success.html',
    aliases: ['/guest-success.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'guest-success-view',
    hasDynamicSegment: false
  },
  {
    path: '/host-success',
    file: 'host-success.html',
    aliases: ['/host-success.html'],
    protected: false,
    cloudflareInternal: true,
    internalName: 'host-success-view',
    hasDynamicSegment: false
  },

  // ===== HOST/LISTING MANAGEMENT =====
  {
    path: '/host-proposals',
    file: 'host-proposals.html',
    aliases: ['/host-proposals.html'],
    protected: true,
    cloudflareInternal: true,
