    11→/**
    12→ * Shared routing logic for both dev and preview servers
    13→ * Uses the Route Registry as single source of truth
    14→ *
    15→ * @param {Object} req - Request object
    16→ * @param {string} publicPrefix - '/public' for dev, '' for preview
    17→ */
    18→function handleRouting(req, publicPrefix = '') {
    19→  const url = req.url || '';
    20→  const [urlPath, queryStringPart] = url.split('?');
    21→  const queryString = queryStringPart ? `?${queryStringPart}` : '';
    22→
    23→  // Special handling for help-center category routes (e.g., /help-center/guests)
    24→  // Must check before general route matching
    25→  // The category is extracted from the URL by the client-side JavaScript
    26→  if (urlPath.startsWith('/help-center/') && !urlPath.includes('.')) {
    27→    req.url = `${publicPrefix}/help-center-category.html${queryString}`;
    28→    return;
    29→  }
    30→
    31→  // Find matching route from registry
    32→  for (const route of routes) {
    33→    const basePath = getBasePath(route);
    34→
    35→    // Check exact match on base path
    36→    if (urlPath === basePath || urlPath === basePath + '/') {
    37→      req.url = `${publicPrefix}/${route.file}${queryString}`;
    38→      return;
    39→    }
    40→
    41→    // Check if URL starts with query string on base path
    42→    if (url.startsWith(basePath + '?')) {
    43→      req.url = `${publicPrefix}/${route.file}${queryString}`;
    44→      return;
    45→    }
    46→
    47→    // Check dynamic segments (e.g., /view-split-lease/123, /account-profile/userId)
    48→    // The dynamic segment (ID) is read from window.location.pathname by client-side JS
    49→    if (route.hasDynamicSegment && urlPath.startsWith(basePath + '/')) {
    50→      req.url = `${publicPrefix}/${route.file}${queryString}`;
    51→      return;
    52→    }
    53→
    54→    // Check aliases (e.g., /faq.html, /index.html)
    55→    if (route.aliases) {
    56→      for (const alias of route.aliases) {
    57→        if (urlPath === alias) {
    58→          req.url = `${publicPrefix}/${route.file}${queryString}`;
    59→          return;
    60→        }
    61→        // Handle alias with additional path/query
    62→        if (urlPath.startsWith(alias)) {
    63→          const remainder = url.substring(alias.length);
    64→          req.url = `${publicPrefix}/${route.file}${remainder}`;
    65→          return;
    66→        }
    67→      }
    68→    }
    69→  }
    70→}
