    75→function copyDirectory(src, dest) {
    76→  if (!fs.existsSync(dest)) {
    77→    fs.mkdirSync(dest, { recursive: true });
    78→  }
    79→
    80→  const entries = fs.readdirSync(src, { withFileTypes: true });
    81→
    82→  for (const entry of entries) {
    83→    const srcPath = path.join(src, entry.name);
    84→    const destPath = path.join(dest, entry.name);
    85→
    86→    if (entry.isDirectory()) {
    87→      copyDirectory(srcPath, destPath);
    88→    } else {
    89→      fs.copyFileSync(srcPath, destPath);
    90→    }
    91→  }
    92→}
