   172→}
   173→
   174→/**
   175→ * Calculate Weekly rental price
   176→ */
   177→function calculateWeeklyPrice(nightsCount, listing, reservationSpan, config, unitMarkup, weeksOffered) {
   178→  const weeklyHostRate = parseFloat(listing.weeklyHostRate || listing['💰Weekly Host Rate'] || 0);
   179→  if (!weeklyHostRate) return { pricePerNight: 0, fourWeekRent: 0, reservationTotal: 0 };
