   125→ */
   126→export function formatStageDisplay(stageId, currentStage) {
   127→  const stage = getStageById(stageId);
   128→  if (!stage) {
   129→    return null;
   130→  }
   131→
   132→  let status = 'pending';
   133→  let statusLabel = 'Pending';
   134→
   135→  if (isStageCompleted(stageId, currentStage)) {
   136→    status = 'completed';
   137→    statusLabel = 'Completed';
   138→  } else if (isCurrentStage(stageId, currentStage)) {
   139→    status = 'current';
   140→    statusLabel = 'In Progress';
   141→  }
   142→
   143→  return {
   144→    ...stage,
   145→    status,
   146→    statusLabel,
   147→    isCompleted: status === 'completed',
   148→    isCurrent: status === 'current',
   149→    isPending: status === 'pending'
   150→  };
   151→}
