     5→/**
     6→ * Format a number as currency (USD)
     7→ * @param {number} amount - The amount to format
     8→ * @returns {string} Formatted currency string
     9→ */
    10→export function formatCurrency(amount) {
    11→  if (amount == null || isNaN(amount)) return '0.00';
    12→  return new Intl.NumberFormat('en-US', {
    13→    minimumFractionDigits: 2,
    14→    maximumFractionDigits: 2
    15→  }).format(amount);
    16→}
    17→
    18→/**
    19→ * Format a date as M/D/YY
    20→ * @param {Date|string} date - The date to format
    21→ * @returns {string} Formatted date string
    22→ */
    23→export function formatDate(date) {
    24→  if (!date) return '';
    25→  const d = new Date(date);
    26→  if (isNaN(d.getTime())) return '';
    27→  const month = d.getMonth() + 1;
    28→  const day = d.getDate();
    29→  const year = d.getFullYear().toString().slice(-2);
    30→  return `${month}/${day}/${year}`;
    31→}
    32→
    33→/**
    34→ * Format a date as full date (e.g., "Mar 28, 2025")
    35→ * @param {Date|string} date - The date to format
    36→ * @returns {string} Formatted date string
    37→ */
    38→export function formatFullDate(date) {
    39→  if (!date) return '';
    40→  const d = new Date(date);
    41→  if (isNaN(d.getTime())) return '';
    42→  return d.toLocaleDateString('en-US', {
    43→    month: 'short',
    44→    day: 'numeric',
    45→    year: 'numeric'
    46→  });
    47→}
    48→
    49→/**
    50→ * Format a date with time (e.g., "Mar 28, 2025 12:00 pm")
    51→ * @param {Date|string} date - The date to format
    52→ * @returns {string} Formatted date/time string
    53→ */
    54→export function formatDateTime(date) {
    55→  if (!date) return '';
    56→  const d = new Date(date);
    57→  if (isNaN(d.getTime())) return '';
    58→  return d.toLocaleDateString('en-US', {
    59→    month: 'short',
    60→    day: 'numeric',
    61→    year: 'numeric',
    62→    hour: 'numeric',
    63→    minute: '2-digit',
    64→    hour12: true
    65→  }).replace(',', '');
    66→}
    67→
    68→/**
    69→ * Format time only (e.g., "2:00 pm")
    70→ * @param {string} time - The time string
    71→ * @returns {string} Formatted time string
    72→ */
    73→export function formatTime(time) {
    74→  if (!time) return '';
    75→  return time.toLowerCase();
    76→}
    77→
    78→/**
    79→ * Format a date range
    80→ * @param {Date|string} start - Start date
    81→ * @param {Date|string} end - End date
    82→ * @returns {string} Formatted date range
    83→ */
    84→export function formatDateRange(start, end) {
    85→  return `${formatDate(start)} - ${formatDate(end)}`;
    86→}
    87→

