import { logger } from '../../../lib/logger.js';

export function navigateToListing(proposal) {
  if (!proposal?.listing?.id && !proposal?.listingId && !proposal?.Listing) {
    logger.error('navigateToListing: No listing ID found', { proposal });
    return;
