  let url = `/messages`;
  const params = new URLSearchParams();

  if (hostId) params.append('recipient', hostId);
  if (proposalId) params.append('proposal', proposalId);

  if (params.toString()) {
    url += `?${params.toString()}`;
  }

  logger.debug('navigateToMessaging', { url, hostId, proposalId });
