     1→/**
     2→ * Calculate check-in and check-out days from selected day indices.
     3→ * Handles wrap-around scenarios (e.g., Fri-Mon selection).
     4→ *
     5→ * NOTE: checkOut is the LAST SELECTED day, not the day AFTER.
     6→ * For checkout as the day after the last stay, use calculateCheckInOutDays.js
     7→ *
     8→ * @param {number[]} selectedDays - Array of day indices (0=Sun, 6=Sat)
     9→ * @returns {{ checkIn: number, checkOut: number }}
    10→ * @throws {Error} If selectedDays is null/undefined or has less than 2 days
    11→ *
    12→ * @see calculateCheckInOutDays for a version that returns checkout as day AFTER last selected
    13→ */
    14→export function calculateCheckInOutFromDays(selectedDays) {
    15→  if (!selectedDays || selectedDays.length < 2) {
    16→    throw new Error('calculateCheckInOutFromDays: selectedDays must contain at least 2 days');
    17→  }
    18→
    19→  const sortedDays = [...selectedDays].sort((a, b) => a - b);
    20→  const hasSaturday = sortedDays.includes(6);
    21→  const hasSunday = sortedDays.includes(0);
    22→  const isWrapAround = hasSaturday && hasSunday && sortedDays.length < 7;
    23→
    24→  if (isWrapAround) {
    25→    // Find the gap to determine actual boundaries
    26→    let gapIndex = -1;
    27→    for (let i = 1; i < sortedDays.length; i++) {
    28→      if (sortedDays[i] - sortedDays[i - 1] > 1) {
    29→        gapIndex = i;
    30→        break;
    31→      }
    32→    }
    33→
    34→    if (gapIndex !== -1) {
    35→      return {
    36→        checkIn: sortedDays[gapIndex],
    37→        checkOut: sortedDays[gapIndex - 1]
    38→      };
    39→    }
    40→  }
    41→
    42→  return {
    43→    checkIn: sortedDays[0],
    44→    checkOut: sortedDays[sortedDays.length - 1]
    45→  };
    46→}
