     1→/**
     2→ * Validate that a value is a non-negative number.
     3→ * @param {*} value - Value to validate
     4→ * @param {string} paramName - Parameter name for error message
     5→ * @param {string} functionName - Function name for error message
     6→ * @throws {Error} If validation fails
     7→ */
     8→export function validateNonNegativeNumber(value, paramName, functionName) {
     9→  if (typeof value !== 'number' || isNaN(value) || value < 0) {
    10→    throw new Error(`${functionName}: ${paramName} must be a non-negative number, got ${typeof value}: ${value}`);
    11→  }
    12→}
    13→
    14→/**
    15→ * Validate that a value is a positive integer.
    16→ * @param {*} value - Value to validate
    17→ * @param {string} paramName - Parameter name for error message
    18→ * @param {string} functionName - Function name for error message
    19→ * @throws {Error} If validation fails
    20→ */
    21→export function validatePositiveInteger(value, paramName, functionName) {
    22→  if (typeof value !== 'number' || !Number.isInteger(value) || value < 1) {
    23→    throw new Error(`${functionName}: ${paramName} must be a positive integer, got ${typeof value}: ${value}`);
    24→  }
    25→}
    26→
    27→/**
    28→ * Validate that a value is a number (allows NaN check).
    29→ * @param {*} value - Value to validate
    30→ * @param {string} paramName - Parameter name for error message
    31→ * @param {string} functionName - Function name for error message
    32→ * @throws {Error} If validation fails
    33→ */
    34→export function validateNumber(value, paramName, functionName) {
    35→  if (typeof value !== 'number' || isNaN(value)) {
    36→    throw new Error(`${functionName}: ${paramName} must be a number, got ${typeof value}`);
    37→  }
    38→}
    39→
    40→/**
    41→ * Validate that a value is a positive number.
    42→ * @param {*} value - Value to validate
    43→ * @param {string} paramName - Parameter name for error message
    44→ * @param {string} functionName - Function name for error message
    45→ * @throws {Error} If validation fails
    46→ */
    47→export function validatePositiveNumber(value, paramName, functionName) {
    48→  if (typeof value !== 'number' || isNaN(value) || value <= 0) {
    49→    throw new Error(`${functionName}: ${paramName} must be positive, got ${value}`);
    50→  }
    51→}
    52→
    53→/**
    54→ * Validate that a value is within a specific range.
    55→ * @param {*} value - Value to validate
    56→ * @param {number} min - Minimum value (inclusive)
    57→ * @param {number} max - Maximum value (inclusive)
    58→ * @param {string} paramName - Parameter name for error message
    59→ * @param {string} functionName - Function name for error message
    60→ * @throws {Error} If validation fails
    61→ */
    62→export function validateRange(value, min, max, paramName, functionName) {
    63→  if (typeof value !== 'number' || isNaN(value) || value < min || value > max) {
    64→    throw new Error(`${functionName}: ${paramName} must be between ${min}-${max} nights, got ${value}`);
    65→  }
    66→}
