     1→/**
     2→ * Determines if the full user name (first + last) should be displayed.
     3→ *
     4→ * @function shouldShowFullName
     5→ * @intent Business rule for when to show full name vs first name only
     6→ * @rule Show full name only if lastName exists and viewport is not mobile
     7→ * @rule Mobile viewports show first name only for space conservation
     8→ * @rule If no lastName, always show first name only
     9→ *
    10→ * @param {object} params - Named parameters
    11→ * @param {string} params.firstName - User's first name (required)
    12→ * @param {string|null|undefined} params.lastName - User's last name (optional)
    13→ * @param {boolean} params.isMobile - Whether the viewport is mobile sized
    14→ * @returns {boolean} True if full name should be displayed
    15→ *
    16→ * @example
    17→ * shouldShowFullName({ firstName: 'John', lastName: 'Doe', isMobile: false }) // true
    18→ * shouldShowFullName({ firstName: 'John', lastName: 'Doe', isMobile: true }) // false
    19→ * shouldShowFullName({ firstName: 'John', lastName: null, isMobile: false }) // false
    20→ */
    21→export function shouldShowFullName({ firstName, lastName, isMobile }) {
    22→  // NO FALLBACK: firstName is required
    23→  if (typeof firstName !== 'string' || firstName.trim().length === 0) {
    24→    throw new Error('shouldShowFullName requires a valid firstName');
    25→  }
    26→
    27→  // Validate isMobile is a boolean
    28→  if (typeof isMobile !== 'boolean') {
    29→    throw new Error('shouldShowFullName requires isMobile to be a boolean');
    30→  }
    31→
    32→  // Don't show full name on mobile (space constraints)
    33→  if (isMobile) {
    34→    return false;
    35→  }
    36→
    37→  // Don't show full name if lastName is missing or empty
    38→  if (lastName === null || lastName === undefined) {
    39→    return false;
    40→  }
    41→
    42→  if (typeof lastName !== 'string' || lastName.trim().length === 0) {
    43→    return false;
    44→  }
    45→
    46→  // Show full name: not mobile AND has valid lastName
    47→  return true;
    48→}
