     1→/**
     2→ * Device Detection Hook
     3→ * Split Lease - Frontend
     4→ *
     5→ * Provides centralized device/viewport detection for responsive behavior.
     6→ * Uses a standard mobile breakpoint of 768px matching common mobile patterns.
     7→ *
     8→ * Usage:
     9→ *   import { useIsMobile, useIsDesktop } from '../hooks/useDeviceDetection';
    10→ *
    11→ *   function MyComponent() {
    12→ *     const isMobile = useIsMobile();
    13→ *     const isDesktop = useIsDesktop();
    14→ *
    15→ *     if (isMobile) {
    16→ *       return <MobileLayout />;
    17→ *     }
    18→ *     return <DesktopLayout />;
    19→ *   }
    20→ */
    21→
    22→import { useState, useEffect } from 'react';
    23→
    24→// Standard mobile breakpoint - viewport widths <= this are considered mobile
    25→const MOBILE_BREAKPOINT = 768;
    26→
    27→// Tablet breakpoint for more granular detection if needed
    28→const TABLET_BREAKPOINT = 1024;
    29→
    30→/**
    31→ * Hook to detect if current viewport is mobile-sized
    32→ * @returns {boolean} True if viewport width <= 768px
    33→ */
    34→export function useIsMobile() {
    35→  const [isMobile, setIsMobile] = useState(() => {
    36→    // SSR safety: default to false if window is not available
    37→    if (typeof window === 'undefined') return false;
    38→    return window.innerWidth <= MOBILE_BREAKPOINT;
    39→  });
    40→
    41→  useEffect(() => {
    42→    const checkMobile = () => {
    43→      setIsMobile(window.innerWidth <= MOBILE_BREAKPOINT);
    44→    };
    45→
    46→    // Initial check
    47→    checkMobile();
    48→
    49→    // Listen for resize events
    50→    window.addEventListener('resize', checkMobile);
    51→
    52→    // Cleanup
    53→    return () => window.removeEventListener('resize', checkMobile);
    54→  }, []);
    55→
    56→  return isMobile;
    57→}
    58→
    59→/**
    60→ * Hook to detect if current viewport is desktop-sized
    61→ * Inverse of useIsMobile - viewport width > 768px
    62→ *
    63→ * @returns {boolean} True if viewport width > 768px
    64→ */
    65→export function useIsDesktop() {
    66→  const [isDesktop, setIsDesktop] = useState(() => {
    67→    if (typeof window === 'undefined') return true;
    68→    return window.innerWidth > MOBILE_BREAKPOINT;
    69→  });
    70→
    71→  useEffect(() => {
    72→    const checkDesktop = () => {
    73→      setIsDesktop(window.innerWidth > MOBILE_BREAKPOINT);
    74→    };
    75→
    76→    checkDesktop();
    77→    window.addEventListener('resize', checkDesktop);
    78→
    79→    return () => window.removeEventListener('resize', checkDesktop);
    80→  }, []);
    81→
    82→  return isDesktop;
    83→}
    84→
    85→/**
    86→ * Hook to detect if current viewport is tablet-sized
    87→ * Tablet range: 769px to 1024px
    88→ *
    89→ * @returns {boolean} True if viewport width is between mobile and tablet breakpoints
    90→ */
    91→export function useIsTablet() {
    92→  const [isTablet, setIsTablet] = useState(() => {
    93→    if (typeof window === 'undefined') return false;
    94→    const width = window.innerWidth;
    95→    return width > MOBILE_BREAKPOINT && width <= TABLET_BREAKPOINT;
    96→  });
    97→
    98→  useEffect(() => {
    99→    const checkTablet = () => {
   100→      const width = window.innerWidth;
   101→      setIsTablet(width > MOBILE_BREAKPOINT && width <= TABLET_BREAKPOINT);
   102→    };
   103→
   104→    checkTablet();
   105→    window.addEventListener('resize', checkTablet);
   106→
   107→    return () => window.removeEventListener('resize', checkTablet);
   108→  }, []);
   109→
   110→  return isTablet;
   111→}
   112→
   113→/**
   114→ * Hook returning device type string
   115→ * Useful for analytics or conditional rendering
   116→ *
   117→ * @returns {'mobile' | 'tablet' | 'desktop'} Current device type
   118→ */
   119→export function useDeviceType() {
   120→  const isMobile = useIsMobile();
   121→  const isTablet = useIsTablet();
   122→
   123→  if (isMobile) return 'mobile';
   124→  if (isTablet) return 'tablet';
   125→  return 'desktop';
   126→}
   127→
   128→/**
   129→ * Hook providing all device detection values at once
   130→ * Useful when multiple checks are needed
   131→ *
   132→ * @returns {Object} Object with isMobile, isTablet, isDesktop, deviceType
   133→ */
   134→export function useDeviceDetection() {
   135→  const isMobile = useIsMobile();
   136→  const isTablet = useIsTablet();
   137→  const isDesktop = useIsDesktop();
   138→
   139→  return {
   140→    isMobile,
   141→    isTablet,
   142→    isDesktop,
   143→    deviceType: isMobile ? 'mobile' : isTablet ? 'tablet' : 'desktop',
   144→    breakpoints: {
   145→      mobile: MOBILE_BREAKPOINT,
   146→      tablet: TABLET_BREAKPOINT
   147→    }
   148→  };
   149→}
   150→
   151→// Export constants for direct use
   152→export { MOBILE_BREAKPOINT, TABLET_BREAKPOINT };
   153→

