     1→/**
     2→ * Input Sanitization Utilities
     3→ * Protects against XSS, SQL injection, and other security vulnerabilities
     4→ *
     5→ * SECURITY PRINCIPLE: Never trust user input
     6→ * - Sanitize all user-provided text
     7→ * - Validate URL parameters
     8→ * - Escape HTML entities
     9→ * - Strip dangerous characters
    10→ */
    11→
    12→/**
    13→ * Sanitize text input by removing potentially dangerous characters
    14→ * @param {string} input - Raw user input
    15→ * @param {object} options - Sanitization options
    16→ * @returns {string} Sanitized string
    17→ */
    18→export function sanitizeText(input, options = {}) {
    19→  if (!input || typeof input !== 'string') {
    20→    return '';
    21→  }
    22→
    23→  const {
    24→    maxLength = 1000,
    25→    allowNewlines = false,
    26→    allowSpecialChars = true
    27→  } = options;
    28→
    29→  let sanitized = input;
    30→
    31→  // Trim whitespace
    32→  sanitized = sanitized.trim();
    33→
    34→  // Limit length
    35→  if (sanitized.length > maxLength) {
    36→    sanitized = sanitized.substring(0, maxLength);
    37→  }
    38→
    39→  // Remove control characters except newlines (if allowed)
    40→  if (allowNewlines) {
    41→    sanitized = sanitized.replace(/[\x00-\x09\x0B-\x0C\x0E-\x1F\x7F]/g, '');
    42→  } else {
    43→    sanitized = sanitized.replace(/[\x00-\x1F\x7F]/g, '');
    44→  }
    45→
    46→  // Remove script tags and event handlers
    47→  sanitized = sanitized.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '');
    48→  sanitized = sanitized.replace(/on\w+\s*=\s*["'][^"']*["']/gi, '');
    49→  sanitized = sanitized.replace(/on\w+\s*=\s*[^\s>]*/gi, '');
    50→
    51→  // Remove javascript: and data: protocols
    52→  sanitized = sanitized.replace(/javascript:/gi, '');
    53→  sanitized = sanitized.replace(/data:text\/html/gi, '');
    54→
    55→  // Remove HTML comments
    56→  sanitized = sanitized.replace(/<!--[\s\S]*?-->/g, '');
    57→
    58→  // Optionally remove special characters (for search inputs)
    59→  if (!allowSpecialChars) {
    60→    sanitized = sanitized.replace(/[<>{}[\]\\\/]/g, '');
    61→  }
    62→
    63→  return sanitized;
    64→}
    65→
    66→/**
    67→ * Sanitize search query input
    68→ * @param {string} query - Search query
    69→ * @returns {string} Sanitized query
    70→ */
    71→export function sanitizeSearchQuery(query) {
    72→  return sanitizeText(query, {
    73→    maxLength: 200,
    74→    allowNewlines: false,
    75→    allowSpecialChars: false
    76→  });
    77→}
    78→
    79→/**
    80→ * Sanitize neighborhood filter input
    81→ * @param {string} input - Neighborhood search input
    82→ * @returns {string} Sanitized input
    83→ */
    84→export function sanitizeNeighborhoodSearch(input) {
    85→  return sanitizeText(input, {
    86→    maxLength: 100,
    87→    allowNewlines: false,
    88→    allowSpecialChars: false
    89→  });
    90→}
    91→
    92→/**
    93→ * Escape HTML entities to prevent XSS
    94→ * @param {string} text - Text to escape
    95→ * @returns {string} Escaped text
    96→ */
    97→export function escapeHtml(text) {
    98→  if (!text || typeof text !== 'string') {
    99→    return '';
   100→  }
   101→
   102→  const map = {
   103→    '&': '&amp;',
   104→    '<': '&lt;',
   105→    '>': '&gt;',
   106→    '"': '&quot;',
   107→    "'": '&#x27;',
