   236→    rateLimitMap.set(key, { count: 1, resetTime: now + windowMs });
   237→    return true;
   238→  }
   239→
   240→  const record = rateLimitMap.get(key);
   241→
   242→  if (now > record.resetTime) {
   243→    // Reset window
   244→    rateLimitMap.set(key, { count: 1, resetTime: now + windowMs });
   245→    return true;
   246→  }
   247→
   248→  if (record.count >= maxRequests) {
   249→    return false; // Rate limit exceeded
   250→  }
   251→
   252→  record.count++;
   253→  return true;
   254→}
