     1→/**
     2→ * Calculate borough match score for a candidate listing.
     3→ *
     4→ * @intent Score geographic proximity between candidate and proposal listings.
     5→ * @rule Same borough = 25 points (full match).
     6→ * @rule Adjacent borough = 15 points (partial match).
     7→ * @rule Different/non-adjacent = 0 points.
     8→ *
     9→ * @param {object} params - Named parameters.
    10→ * @param {object} params.candidateListing - Candidate listing object.
    11→ * @param {object} params.proposal - Proposal object with nested listing data.
    12→ * @returns {number} Score from 0-25.
    13→ *
    14→ * @example
    15→ * calculateBoroughScore({
    16→ *   candidateListing: { boroughName: 'Manhattan' },
    17→ *   proposal: { listing: { boroughName: 'Manhattan' } }
    18→ * })
    19→ * // => 25 (exact match)
    20→ *
    21→ * calculateBoroughScore({
    22→ *   candidateListing: { boroughName: 'Brooklyn' },
    23→ *   proposal: { listing: { boroughName: 'Manhattan' } }
    24→ * })
    25→ * // => 15 (adjacent)
    26→ */
    27→import { isBoroughAdjacent } from '../../rules/matching/isBoroughAdjacent.js';
    28→import { MATCH_WEIGHTS } from './constants.js';
    29→
    30→export function calculateBoroughScore({ candidateListing, proposal }) {
    31→  if (!candidateListing || !proposal) {
    32→    return 0;
    33→  }
    34→
    35→  // Extract borough names from both objects, checking multiple possible field names
    36→  const candidateBorough =
    37→    candidateListing.boroughName ||
    38→    candidateListing['Location - Borough'] ||
    39→    null;
    40→
    41→  const proposalBorough =
    42→    proposal.listing?.boroughName ||
    43→    proposal.listing?.borough ||
    44→    proposal.listing?.['Location - Borough'] ||
    45→    null;
    46→
    47→  if (!candidateBorough || !proposalBorough) {
    48→    return 0;
    49→  }
    50→
    51→  const normalizedCandidate = candidateBorough.toLowerCase().trim();
    52→  const normalizedProposal = proposalBorough.toLowerCase().trim();
    53→
    54→  // Exact match = full points
    55→  if (normalizedCandidate === normalizedProposal) {
    56→    return MATCH_WEIGHTS.BOROUGH;
    57→  }
    58→
    59→  // Adjacent match = partial points (60% of full)
    60→  if (isBoroughAdjacent({ borough1: normalizedProposal, borough2: normalizedCandidate })) {
    61→    return 15;
    62→  }
    63→
    64→  // No match
    65→  return 0;
    66→}
