     1→/**
     2→ * Calculate duration match score for a candidate listing.
     3→ *
     4→ * @intent Score how well listing's minimum nights aligns with proposal duration.
     5→ * @rule Exact match or within tolerance = 10 points.
     6→ * @rule Outside tolerance = 0 points.
     7→ * @rule Uses isDurationMatch rule for determination.
     8→ *
     9→ * @param {object} params - Named parameters.
    10→ * @param {object} params.candidateListing - Candidate listing object.
    11→ * @param {object} params.proposal - Proposal object with nights per week.
    12→ * @returns {number} Score: 0 or 10.
    13→ *
    14→ * @example
    15→ * calculateDurationScore({
    16→ *   candidateListing: { 'Minimum Nights': 4 },
    17→ *   proposal: { nightsPerWeek: 4 }
    18→ * })
    19→ * // => 10 (exact match)
    20→ *
    21→ * calculateDurationScore({
    22→ *   candidateListing: { 'Minimum Nights': 2 },
    23→ *   proposal: { nightsPerWeek: 5 }
    24→ * })
    25→ * // => 0 (difference of 3 exceeds tolerance)
    26→ */
    27→import { isDurationMatch } from '../../rules/matching/isDurationMatch.js';
    28→import { MATCH_WEIGHTS } from './constants.js';
    29→
    30→export function calculateDurationScore({ candidateListing, proposal }) {
    31→  if (!candidateListing || !proposal) {
    32→    return 0;
    33→  }
    34→
    35→  const isMatch = isDurationMatch({
    36→    listing: candidateListing,
    37→    proposal
    38→  });
    39→
    40→  return isMatch ? MATCH_WEIGHTS.DURATION : 0;
    41→}
