     1→/**
     2→ * Calculate price proximity score for a candidate listing.
     3→ *
     4→ * @intent Score how close candidate pricing is to proposal budget.
     5→ * @rule Within 10% = 20 points (full score).
     6→ * @rule Within 20% = 15 points.
     7→ * @rule Within 30% = 10 points.
     8→ * @rule Within 50% = 5 points.
     9→ * @rule Over 50% = 0 points.
    10→ *
    11→ * @param {object} params - Named parameters.
    12→ * @param {object} params.candidateListing - Candidate listing object with pricing fields.
    13→ * @param {object} params.proposal - Proposal object with nightly price and nights per week.
    14→ * @returns {number} Score from 0-20.
    15→ *
    16→ * @example
    17→ * calculatePriceScore({
    18→ *   candidateListing: { '💰Nightly Host Rate for 4 nights': 105 },
    19→ *   proposal: { nightlyPrice: 100, nightsPerWeek: 4 }
    20→ * })
    21→ * // => 20 (5% difference, within 10%)
    22→ */
    23→import { getNightlyRateByFrequency } from '../pricing/getNightlyRateByFrequency.js';
    24→import { calculatePriceProximity } from './calculatePriceProximity.js';
    25→import { MATCH_WEIGHTS, PRICE_THRESHOLDS } from './constants.js';
    26→
    27→export function calculatePriceScore({ candidateListing, proposal }) {
    28→  if (!candidateListing || !proposal) {
    29→    return 0;
    30→  }
    31→
    32→  // Determine nights per week from proposal
    33→  const nightsPerWeek =
    34→    proposal.nightsPerWeek ||
    35→    proposal.daysSelected?.length ||
    36→    4; // Default to 4 if not specified
    37→
    38→  // Get proposal nightly price
    39→  const proposalNightlyRate = proposal.nightlyPrice;
    40→
    41→  if (!proposalNightlyRate || proposalNightlyRate <= 0) {
    42→    return 0;
    43→  }
    44→
    45→  // Get candidate nightly rate for same frequency
    46→  let candidateNightlyRate;
    47→  try {
    48→    candidateNightlyRate = getNightlyRateByFrequency({
    49→      listing: candidateListing,
    50→      nightsSelected: nightsPerWeek
    51→    });
    52→  } catch {
    53→    // If we can't get a rate for this frequency, return 0
    54→    return 0;
    55→  }
    56→
    57→  if (!candidateNightlyRate || candidateNightlyRate <= 0) {
    58→    return 0;
    59→  }
    60→
    61→  // Calculate proximity ratio
    62→  let proximity;
    63→  try {
    64→    proximity = calculatePriceProximity({
    65→      candidateNightlyRate,
    66→      proposalNightlyRate
    67→    });
    68→  } catch {
    69→    return 0;
    70→  }
    71→
    72→  // Score based on proximity thresholds
    73→  if (proximity <= PRICE_THRESHOLDS.WITHIN_10_PERCENT) {
    74→    return MATCH_WEIGHTS.PRICE; // 20 points
    75→  }
    76→
    77→  if (proximity <= PRICE_THRESHOLDS.WITHIN_20_PERCENT) {
    78→    return 15;
    79→  }
    80→
    81→  if (proximity <= PRICE_THRESHOLDS.WITHIN_30_PERCENT) {
    82→    return 10;
    83→  }
    84→
    85→  if (proximity <= PRICE_THRESHOLDS.WITHIN_50_PERCENT) {
    86→    return 5;
    87→  }
    88→
    89→  return 0;
    90→}
    91→

