     1→/**
     2→ * Calculate weekly stay support score for a candidate listing.
     3→ *
     4→ * @intent Score listing's ability to accommodate 7-night stays.
     5→ * @rule Supports weekly stays = 15 points.
     6→ * @rule Does not support = 0 points.
     7→ * @rule Uses supportsWeeklyStays rule for determination.
     8→ *
     9→ * @param {object} params - Named parameters.
    10→ * @param {object} params.candidateListing - Candidate listing object.
    11→ * @returns {number} Score: 0 or 15.
    12→ *
    13→ * @example
    14→ * calculateWeeklyStayScore({
    15→ *   candidateListing: {
    16→ *     'Schedule days available': [0, 1, 2, 3, 4, 5, 6],
    17→ *     'Minimum Nights': 3
    18→ *   }
    19→ * })
    20→ * // => 15 (supports weekly stays)
    21→ *
    22→ * calculateWeeklyStayScore({
    23→ *   candidateListing: {
    24→ *     'Schedule days available': [1, 2, 3, 4, 5],
    25→ *     'Minimum Nights': 2
    26→ *   }
    27→ * })
    28→ * // => 0 (does not support weekly stays)
    29→ */
    30→import { supportsWeeklyStays } from '../../rules/matching/supportsWeeklyStays.js';
    31→import { MATCH_WEIGHTS } from './constants.js';
    32→
    33→export function calculateWeeklyStayScore({ candidateListing }) {
    34→  if (!candidateListing) {
    35→    return 0;
    36→  }
    37→
    38→  const supports = supportsWeeklyStays({ listing: candidateListing });
    39→
    40→  return supports ? MATCH_WEIGHTS.WEEKLY_STAY : 0;
    41→}
