     1→/**
     2→ * Check if listing's minimum nights closely matches proposal duration.
     3→ *
     4→ * @intent Determine if listing and proposal have compatible duration expectations.
     5→ * @rule Match if difference between minimum nights and proposal nights <= tolerance.
     6→ * @rule Used for scoring, not hard filtering.
     7→ * @rule Missing minimum nights treated as match (flexible listing).
     8→ *
     9→ * @param {object} params - Named parameters.
    10→ * @param {object} params.listing - Listing object with minimum nights.
    11→ * @param {object} params.proposal - Proposal object with nights per week.
    12→ * @param {number} [params.tolerance=1] - Maximum allowed difference in nights.
    13→ * @returns {boolean} True if durations match within tolerance.
    14→ *
    15→ * @example
    16→ * isDurationMatch({
    17→ *   listing: { 'Minimum Nights': 4 },
    18→ *   proposal: { nightsPerWeek: 4 }
    19→ * })
    20→ * // => true (exact match)
    21→ *
    22→ * isDurationMatch({
    23→ *   listing: { 'Minimum Nights': 4 },
    24→ *   proposal: { nightsPerWeek: 5 }
    25→ * })
    26→ * // => true (within tolerance of 1)
    27→ *
    28→ * isDurationMatch({
    29→ *   listing: { 'Minimum Nights': 3 },
    30→ *   proposal: { nightsPerWeek: 6 }
    31→ * })
    32→ * // => false (difference of 3 exceeds tolerance)
    33→ */
    34→export function isDurationMatch({ listing, proposal, tolerance = 1 }) {
    35→  if (!listing || !proposal) {
    36→    return false;
    37→  }
    38→
    39→  const listingMinNights = listing['Minimum Nights'];
    40→  const proposalNights =
    41→    proposal.nightsPerWeek ||
    42→    proposal.daysSelected?.length ||
    43→    0;
    44→
    45→  // If listing has no minimum nights, consider it a flexible match
    46→  if (listingMinNights === null || listingMinNights === undefined) {
    47→    return true;
    48→  }
    49→
    50→  if (typeof listingMinNights !== 'number') {
    51→    return true; // Invalid data treated as flexible
    52→  }
    53→
    54→  if (proposalNights === 0) {
    55→    return false;
    56→  }
    57→
    58→  // Check if difference is within tolerance
    59→  const difference = Math.abs(listingMinNights - proposalNights);
    60→
    61→  return difference <= tolerance;
    62→}
    63→

