     1→/**
     2→ * Schedule contiguity rules
     3→ *
     4→ * Validates that selected days form consecutive nights for split lease stays.
     5→ */
     6→
     7→/**
     8→ * All valid day indices (0=Sunday through 6=Saturday)
     9→ * @type {number[]}
    10→ */
    11→const ALL_DAYS = [0, 1, 2, 3, 4, 5, 6];
    12→
    13→/**
    14→ * Validate that a day index is valid (0-6)
    15→ *
    16→ * @param {*} day - Value to validate
    17→ * @returns {boolean} Whether the day is a valid index
    18→ */
    19→const isValidDayIndex = (day) =>
    20→  typeof day === 'number' && !isNaN(day) && day >= 0 && day <= 6;
    21→
    22→/**
    23→ * Check if an array of sorted days is contiguous (no gaps)
    24→ *
    25→ * @param {number[]} sortedDays - Sorted array of day indices
    26→ * @returns {boolean} Whether days are consecutive
    27→ */
    28→const isConsecutive = (sortedDays) =>
    29→  sortedDays.every((day, index) =>
    30→    index === 0 || day === sortedDays[index - 1] + 1
    31→  );
    32→
    33→/**
    34→ * Check if unselected days form a contiguous block
    35→ * (if they do, the selected days properly wrap around the week)
    36→ *
    37→ * @param {number[]} sortedSelectedDays - Sorted array of selected day indices
    38→ * @returns {boolean} Whether unselected days are contiguous
    39→ */
    40→const areUnselectedDaysContiguous = (sortedSelectedDays) => {
    41→  const selectedSet = new Set(sortedSelectedDays);
    42→  const unselectedDays = ALL_DAYS.filter((d) => !selectedSet.has(d));
    43→
    44→  // If all days selected, wrap-around is trivially true
    45→  if (unselectedDays.length === 0) {
    46→    return true;
    47→  }
    48→
    49→  // Check if unselected days are consecutive
    50→  return isConsecutive(unselectedDays);
    51→};
    52→
    53→/**
    54→ * Check if selected days form a contiguous (consecutive) block.
    55→ *
    56→ * @intent Enforce the business rule that split lease stays must be consecutive nights.
    57→ * @rule Days must be consecutive (Mon-Fri OK, Mon+Wed NOT OK).
    58→ * @rule Handles week wrap-around cases (Fri-Sun OK, Sat-Tue OK).
    59→ * @rule For wrap-around, uses inverse logic: if unselected days are contiguous, selected days wrap properly.
    60→ * @rule 6 or more days is always contiguous (only 1 gap maximum possible).
    61→ *
    62→ * @param {object} params - Named parameters.
    63→ * @param {number[]} params.selectedDayIndices - Array of day indices (0=Sunday through 6=Saturday).
    64→ * @returns {boolean} True if days are contiguous, false otherwise.
    65→ *
    66→ * @throws {Error} If selectedDayIndices is not an array.
    67→ * @throws {Error} If any day index is invalid (not 0-6).
    68→ *
    69→ * @example
    70→ * isScheduleContiguous({ selectedDayIndices: [1, 2, 3, 4, 5] }) // true (Mon-Fri)
    71→ * isScheduleContiguous({ selectedDayIndices: [1, 3, 5] }) // false (Mon, Wed, Fri - gaps)
    72→ * isScheduleContiguous({ selectedDayIndices: [5, 6, 0] }) // true (Fri-Sun, wraps)
    73→ * isScheduleContiguous({ selectedDayIndices: [6, 0, 1, 2] }) // true (Sat-Tue, wraps)
    74→ */
    75→export function isScheduleContiguous({ selectedDayIndices }) {
    76→  // No Fallback: Validate input type
    77→  if (!Array.isArray(selectedDayIndices)) {
    78→    throw new Error(
    79→      `isScheduleContiguous: selectedDayIndices must be an array, got ${typeof selectedDayIndices}`
    80→    );
    81→  }
    82→
    83→  // Empty selection is not contiguous
    84→  if (selectedDayIndices.length === 0) {
    85→    return false;
    86→  }
    87→
    88→  // Single day is contiguous
    89→  if (selectedDayIndices.length === 1) {
    90→    return true;
    91→  }
    92→
    93→  // Validate all day indices (using functional check)
    94→  const invalidDay = selectedDayIndices.find((day) => !isValidDayIndex(day));
    95→  if (invalidDay !== undefined) {
    96→    throw new Error(
    97→      `isScheduleContiguous: Invalid day index ${invalidDay}, must be 0-6`
    98→    );
    99→  }
   100→
   101→  const sorted = [...selectedDayIndices].sort((a, b) => a - b);
   102→
   103→  // If 6 or more days selected, it's contiguous (at most 1 gap)
   104→  if (sorted.length >= 6) {
   105→    return true;
   106→  }
   107→
   108→  // Check for standard contiguous sequence (no wrap-around)
   109→  if (isConsecutive(sorted)) {
   110→    return true;
   111→  }
   112→
   113→  // Check for wrap-around case (selection includes both Sunday=0 and Saturday=6)
   114→  const hasZero = sorted[0] === 0;
   115→  const hasSix = sorted[sorted.length - 1] === 6;
   116→
   117→  if (hasZero && hasSix) {
   118→    // Week wrap-around: check if unselected days are contiguous
   119→    return areUnselectedDaysContiguous(sorted);
   120→  }
   121→
   122→  return false;
   123→}
