     1→/**
     2→ * Normalizes and validates a profile photo URL.
     3→ *
     4→ * @function processProfilePhotoUrl
     5→ * @intent Transform raw photo URL into validated, normalized format
     6→ * @rule Handle protocol-relative URLs (//example.com/photo.jpg → https://example.com/photo.jpg)
     7→ * @rule Return null for invalid or missing URLs (NO FALLBACK to placeholder)
     8→ * @rule Validate URL format to prevent XSS (must be http/https)
     9→ * @rule Empty strings are treated as null (no photo)
    10→ *
    11→ * @param {object} params - Named parameters
    12→ * @param {string|null|undefined} params.photoUrl - Raw profile photo URL
    13→ * @returns {string|null} Normalized URL or null if invalid/missing
    14→ *
    15→ * @example
    16→ * processProfilePhotoUrl({ photoUrl: 'https://example.com/photo.jpg' }) // "https://example.com/photo.jpg"
    17→ * processProfilePhotoUrl({ photoUrl: '//example.com/photo.jpg' }) // "https://example.com/photo.jpg"
    18→ * processProfilePhotoUrl({ photoUrl: '' }) // null
    19→ * processProfilePhotoUrl({ photoUrl: null }) // null
    20→ * processProfilePhotoUrl({ photoUrl: 'javascript:alert(1)' }) // null (XSS prevention)
    21→ */
    22→export function processProfilePhotoUrl({ photoUrl }) {
    23→  // NO FALLBACK: Return null for missing/invalid, never a placeholder
    24→  if (photoUrl === null || photoUrl === undefined) {
    25→    return null;
    26→  }
    27→
    28→  // Type check
    29→  if (typeof photoUrl !== 'string') {
    30→    return null;
    31→  }
    32→
    33→  // Trim whitespace
    34→  const trimmedUrl = photoUrl.trim();
    35→
    36→  // Empty string is treated as no photo
    37→  if (trimmedUrl.length === 0) {
    38→    return null;
    39→  }
    40→
    41→  // Handle protocol-relative URLs (//example.com/photo.jpg)
    42→  if (trimmedUrl.startsWith('//')) {
    43→    return `https:${trimmedUrl}`;
    44→  }
    45→
    46→  // Validate that URL starts with http:// or https:// (XSS prevention)
    47→  if (!trimmedUrl.startsWith('http://') && !trimmedUrl.startsWith('https://')) {
    48→    // Invalid protocol (could be javascript:, data:, etc.)
    49→    return null;
    50→  }
    51→
    52→  // Return normalized URL
    53→  return trimmedUrl;
    54→}
