     1→/**
     2→ * Matching Calculators
     3→ *
     4→ * Pure calculation functions for Quick Match scoring algorithm.
     5→ * Maximum possible score is 95 points (price drop heuristic not implemented).
     6→ *
     7→ * @module logic/calculators/matching
     8→ */
     9→
    10→// Master scoring functions
    11→export { calculateMatchScore } from './calculateMatchScore.js';
    12→export { calculateMatchHeuristics } from './calculateMatchHeuristics.js';
    13→
    14→// Individual score calculators
    15→export { calculateBoroughScore } from './calculateBoroughScore.js';
    16→export { calculatePriceScore } from './calculatePriceScore.js';
