     1→/**
     2→ * Calculate schedule overlap score for a candidate listing.
     3→ *
     4→ * @intent Score how well candidate's available days match proposal's requested days.
     5→ * @rule Score proportional to overlap ratio.
     6→ * @rule 100% overlap = 20 points (full score).
     7→ * @rule 0% overlap = 0 points.
     8→ * @rule Both use 0-indexed days (0=Sunday through 6=Saturday).
     9→ *
    10→ * @param {object} params - Named parameters.
    11→ * @param {object} params.candidateListing - Candidate listing with schedule data.
    12→ * @param {object} params.proposal - Proposal with days selected.
    13→ * @returns {number} Score from 0-20.
    14→ *
    15→ * @example
    16→ * calculateScheduleOverlapScore({
    17→ *   candidateListing: { 'Schedule days available': [1, 2, 3, 4, 5] },
    18→ *   proposal: { daysSelected: [1, 2, 3, 4] }
    19→ * })
    20→ * // => 20 (100% of requested days available)
    21→ *
    22→ * calculateScheduleOverlapScore({
    23→ *   candidateListing: { 'Schedule days available': [1, 2, 5, 6] },
    24→ *   proposal: { daysSelected: [1, 2, 3, 4] }
    25→ * })
    26→ * // => 10 (50% overlap, 2 of 4 days)
    27→ */
    28→import { hasScheduleCompatibility } from '../../rules/matching/hasScheduleCompatibility.js';
    29→import { MATCH_WEIGHTS } from './constants.js';
    30→
    31→export function calculateScheduleOverlapScore({ candidateListing, proposal }) {
    32→  if (!candidateListing || !proposal) {
    33→    return 0;
    34→  }
    35→
    36→  const { compatible, overlapDays, requestedDays } = hasScheduleCompatibility({
    37→    candidateListing,
    38→    proposal
    39→  });
    40→
    41→  if (!compatible || requestedDays === 0) {
    42→    return 0;
    43→  }
    44→
    45→  // Calculate overlap ratio (0.0 to 1.0)
    46→  const overlapRatio = overlapDays / requestedDays;
    47→
    48→  // Scale to max score and round
    49→  const score = Math.round(overlapRatio * MATCH_WEIGHTS.SCHEDULE);
    50→
    51→  return score;
    52→}
    53→

