     1→/**
     2→ * Calculate host verification score for a candidate listing.
     3→ *
     4→ * @intent Score host trustworthiness based on verification status.
     5→ * @rule 3 verifications = 5 points (full score).
     6→ * @rule 2 verifications = 3 points.
     7→ * @rule 1 verification = 1 point.
     8→ * @rule 0 verifications = 0 points.
     9→ * @rule Used as proxy for host responsiveness (no response rate data).
    10→ *
    11→ * @param {object} params - Named parameters.
    12→ * @param {object} params.hostData - Host/user object with verification fields.
    13→ * @returns {number} Score from 0-5.
    14→ *
    15→ * @example
    16→ * calculateHostScore({
    17→ *   hostData: {
    18→ *     'Verify - Linked In ID': true,
    19→ *     'Verify - Phone': true,
    20→ *     'user verified?': true
    21→ *   }
    22→ * })
    23→ * // => 5 (3 verifications)
    24→ *
    25→ * calculateHostScore({
    26→ *   hostData: {
    27→ *     'Verify - Linked In ID': true,
    28→ *     'Verify - Phone': false,
    29→ *     'user verified?': false
    30→ *   }
    31→ * })
    32→ * // => 1 (1 verification)
    33→ */
    34→import { countHostVerifications } from '../../rules/matching/isVerifiedHost.js';
    35→import { MATCH_WEIGHTS } from './constants.js';
    36→
    37→export function calculateHostScore({ hostData }) {
    38→  if (!hostData) {
    39→    return 0;
    40→  }
    41→
    42→  const verificationCount = countHostVerifications({ host: hostData });
    43→
    44→  // Score mapping based on verification count
    45→  switch (verificationCount) {
    46→    case 3:
    47→      return MATCH_WEIGHTS.HOST; // 5 points
    48→    case 2:
    49→      return 3;
    50→    case 1:
    51→      return 1;
    52→    default:
    53→      return 0;
    54→  }
    55→}
