     1→/**
     2→ * Check if candidate listing schedule is compatible with proposal.
     3→ *
     4→ * @intent Determine if listing has available days that overlap with proposal's requested days.
     5→ * @rule Both listing and proposal use 0-indexed days (0=Sunday through 6=Saturday).
     6→ * @rule Compatible if at least one requested day is available.
     7→ * @rule Returns overlap count for partial scoring.
     8→ *
     9→ * @param {object} params - Named parameters.
    10→ * @param {object} params.candidateListing - Candidate listing with schedule data.
    11→ * @param {object} params.proposal - Proposal with days selected.
    12→ * @returns {object} { compatible: boolean, overlapDays: number, requestedDays: number }
    13→ *
    14→ * @example
    15→ * hasScheduleCompatibility({
    16→ *   candidateListing: { 'Schedule days available': [1, 2, 3, 4, 5] },
    17→ *   proposal: { daysSelected: [1, 2, 3] }
    18→ * })
    19→ * // => { compatible: true, overlapDays: 3, requestedDays: 3 }
    20→ *
    21→ * hasScheduleCompatibility({
    22→ *   candidateListing: { 'Schedule days available': [5, 6] },
    23→ *   proposal: { daysSelected: [1, 2, 3] }
    24→ * })
    25→ * // => { compatible: false, overlapDays: 0, requestedDays: 3 }
    26→ */
    27→export function hasScheduleCompatibility({ candidateListing, proposal }) {
    28→  // Default return for invalid input
    29→  const noCompatibility = { compatible: false, overlapDays: 0, requestedDays: 0 };
    30→
    31→  if (!candidateListing || !proposal) {
    32→    return noCompatibility;
    33→  }
    34→
    35→  // Get listing's available days
    36→  const listingDays = candidateListing['Schedule days available'] || [];
    37→
    38→  // Get proposal's requested days
    39→  const proposalDays = proposal.daysSelected || [];
    40→
    41→  // Validate arrays
    42→  if (!Array.isArray(listingDays) || !Array.isArray(proposalDays)) {
    43→    return noCompatibility;
    44→  }
    45→
    46→  const requestedDays = proposalDays.length;
    47→
    48→  if (requestedDays === 0) {
    49→    return { compatible: false, overlapDays: 0, requestedDays: 0 };
    50→  }
    51→
    52→  // Convert listing days to Set for O(1) lookup
    53→  const listingDaySet = new Set(listingDays);
    54→
    55→  // Count overlapping days
    56→  const overlapDays = proposalDays.filter(day => listingDaySet.has(day)).length;
    57→
    58→  // Compatible if at least one day overlaps
    59→  const compatible = overlapDays > 0;
    60→
    61→  return {
    62→    compatible,
    63→    overlapDays,
    64→    requestedDays
    65→  };
    66→}
    67→

